	// step 2
	PdfWriter writer = PdfWriter.getInstance(document, new FileStream("Chap02_hyphenation.pdf", FileMode.Create));
	// step 3
	document.open();
	// step 4
	String text = "It was the best of times, it was the worst of times, " + 
		"it was the age of wisdom, it was the age of foolishness, " +
		"it was the epoch of belief, it was the epoch of incredulity, " +
		"it was the season of Light, it was the season of Darkness, " +
		"it was the spring of hope, it was the winter of despair, " +
		"we had everything before us, we had nothing before us, " +
		"we were all going direct to Heaven, we were all going direct " +
		"the other way\u2014in short, the period was so far like the present " +
		"period, that some of its noisiest authorities insisted on its " +
		"being received, for good or for evil, in the superlative degree " +
		"of comparison only.";
	document.add(new Paragraph("GB"));
	Chunk ckEN = new Chunk(text);
	HyphenationAuto autoEN = new HyphenationAuto("en", "GB", 2, 2);
	ckEN.setHyphenation(autoEN);
	Paragraph pEN = new Paragraph(ckEN);
	pEN.setAlignment(ElementConst.ALIGN_JUSTIFIED);
	document.add(pEN);
	document.add(new Paragraph("US"));
	Chunk ckUS = new Chunk(text);
	HyphenationAuto autoUS = new HyphenationAuto("en", "US", 2, 2);
	ckUS.setHyphenation(autoUS);
	Paragraph pUS = new Paragraph(ckUS);
	pUS.setAlignment(ElementConst.ALIGN_JUSTIFIED);
	document.add(pUS);

	// step 5
	document.close();
}


// we override the onChapter method
public void onChapter(PdfWriter writer,Document document,float paragraphPosition,Paragraph title) {
    StringBuffer buf = new StringBuffer();
    for (Iterator i = title.getChunks().iterator(); i.hasNext(); ) {
        Chunk chunk = (Chunk) i.next();
        buf.append(chunk.content());
    for (Iterator i = title.getChunks().iterator(); i.hasNext(); ) {
        Chunk chunk = (Chunk) i.next();
        buf.append(chunk.content());
    }
    act = buf.toString();
}


// we override the onChapter method
public override void onChapter(PdfWriter writer,Document document,float paragraphPosition,Paragraph title) 
{
	System.Text.StringBuilder buf = new System.Text.StringBuilder();
	for (Iterator i = title.getChunks().iterator(); i.hasNext(); ) 
	{
		Chunk chunk = (Chunk) i.next();
		buf.Append(chunk.content());
	}
	for (Iterator i = title.getChunks().iterator(); i.hasNext(); ) 
	{
		Chunk chunk = (Chunk) i.next();
		buf.Append(chunk.content());
	}
	act = buf.ToString();
}


/**
 * @see com.lowagie.text.SplitCharacter#isSplitCharacter(int, int, int, char[], com.lowagie.text.pdf.PdfChunk[])
 */
public boolean isSplitCharacter(int start, int current, int end, char[] cc, PdfChunk[] ck) {
	char c;
	if (ck == null)
		c = cc[current];
	else
		c = ck[Math.min(current, ck.length - 1)].getUnicodeEquivalent(cc[current]);
	return (c == '.');
}


public bool isSplitCharacter(int start, int current, int end, char[] cc, PdfChunk[] ck) 
{
	char c;
	if (ck == null)
		c = cc[current];
	else
		c = ck[Math.Min(current, ck.Length - 1)].getUnicodeEquivalent(cc[current]);
	return (c == '.');
}


public int compareTo(Object o) {
    Speaker otherSpeaker = (Speaker) o;
    if (otherSpeaker.name().equals(name)) {
        this.occurrance += otherSpeaker.occurrance();
        otherSpeaker.occurrance = this.occurrance;
        return 0;
    }
    return name.compareTo(otherSpeaker.name());
}


public int compareTo(Object o) 
{
	Speaker otherSpeaker = (Speaker) o;
	if (otherSpeaker.name().Equals(m_name)) 
	{
		this.m_occurrance += otherSpeaker.occurrance();
		otherSpeaker.m_occurrance = this.m_occurrance;
		return 0;
	}
	return m_name.CompareTo(otherSpeaker.name());
}

