/**
 * General example using table events.
 * @param args
 * 		no arguments needed
 */
public static void main(String[] args) throws Exception {

	System.out.println("TableEvents 1");
	// step1
	Document document = new Document(PageSize.A4, 50, 50, 50, 50);
	// step2
	PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("TableEvents1.pdf"));
	// step3
	document.open();
	// step4
	PdfPTable table = new PdfPTable(4);
	table.getDefaultCell().setBorder(Rectangle.NO_BORDER);
	for (int k = 0; k < 24; ++k) {
		if (k != 0)
			table.addCell(String.valueOf(k));
		else
			table.addCell("This is an URL");
	for (int k = 0; k < 24; ++k) {
		if (k != 0)
			table.addCell(String.valueOf(k));
		else
			table.addCell("This is an URL");
	}
	TableEvents1 event = new TableEvents1();
	table.setTableEvent(event);

	// add the table with document add
	document.add(table);
	// add the table at an absolute position
	table.setTotalWidth(300);
	table.writeSelectedRows(0, -1, 100, 600, writer.getDirectContent());

	document.newPage();

	table = new PdfPTable(4);
	float fontSize = 12;
	BaseFont bf = BaseFont.createFont("Helvetica", "winansi", false);
	table.getDefaultCell().setPaddingTop(bf.getFontDescriptor(BaseFont.ASCENT, fontSize) - fontSize + 2);
	table.getDefaultCell().setBorder(Rectangle.NO_BORDER);
	for (int k = 0; k < 500 * 4; ++k) {
		if (k == 0)
			table.addCell(new Phrase("This is an URL", new Font(bf, fontSize)));
		else
			table.addCell(new Phrase(String.valueOf(k), new Font(bf, fontSize)));
	for (int k = 0; k < 500 * 4; ++k) {
		if (k == 0)
			table.addCell(new Phrase("This is an URL", new Font(bf, fontSize)));
		else
			table.addCell(new Phrase(String.valueOf(k), new Font(bf, fontSize)));
	}
	table.setTableEvent(event);
	table.setHeaderRows(3);
	document.add(table);
	// step5
	document.close();
}

{        
	Console.WriteLine("Chapter 1 example 10

	Document document = new Document(PageSize.A4, 50, 50, 50, 50);
	PdfWriter writer = PdfWriter.getInstance(document, new FileStream("Chap0110.pdf", FileMode.Create));
	writer.setEncryption(PdfWriter.STRENGTH128BITS, "userpass", "ownerpass", PdfWriter.AllowCopy | PdfWriter.AllowPrinting);
	document.open();
	document.add(new Paragraph("This document is Top Secret!"));
	document.close();
}


    public void tableLayout(PdfPTable table, float[][] width, float[] heights, int headerRows, int rowStart, PdfContentByte[] canvases) {
        float widths[] = width[0];
        PdfContentByte cb = canvases[PdfPTable.TEXTCANVAS];
        cb.saveState();
        cb.setLineWidth(2);
        cb.setRGBColorStroke(255, 0, 0);
        cb.rectangle(widths[0], heights[heights.length - 1], widths[widths.length - 1] - widths[0], heights[0] - heights[heights.length - 1]);
        cb.stroke();
        if (headerRows > 0) {
            float headerHeight = heights[0];
            for (int k = 0; k < headerRows; ++k)
                headerHeight += heights[k];
            cb.setRGBColorStroke(0, 0, 255);
            cb.rectangle(widths[0], heights[headerRows], widths[widths.length - 1] - widths[0], heights[0] - heights[headerRows]);
            cb.stroke();
        }
        cb.restoreState();
        cb = canvases[PdfPTable.BASECANVAS];
        cb.saveState();
        cb.setLineWidth(.5f);
        for (int line = 0; line < heights.length - 1; ++line) {
            widths = width[line];
            for (int col = 0; col < widths.length - 1; ++col) {
                if (line == 0 && col == 0)
                    cb.setAction(new PdfAction("http
                        widths[col], heights[line + 1], widths[col + 1], heights[line]);
                cb.setRGBColorStrokeF((float)Math.random(), (float)Math.random(), (float)Math.random());
                cb.moveTo(widths[col], heights[line]);
                cb.lineTo(widths[col + 1], heights[line]);
                cb.stroke();
                cb.setRGBColorStrokeF((float)Math.random(), (float)Math.random(), (float)Math.random());
                cb.moveTo(widths[col], heights[line]);
                cb.lineTo(widths[col], heights[line + 1]);
                cb.stroke();
            }
        }
        cb.restoreState();
    }
}

public class Chap1202 {

    public static void main(String[] args) throws Exception {

        System.out.println("Chapter 12 example 2

        // step 1
        Document document = new Document(PageSize.A4, 50, 50, 50, 50);
        try {
            // step 2
            PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("Chap1202.pdf"));
            // step 3
            document.open();
            // step 4
            BaseFont bf = BaseFont.createFont(BaseFont.HELVETICA, BaseFont.WINANSI, BaseFont.NOT_EMBEDDED);
            // table 1
            PdfPTable table = new PdfPTable(4);
            table.getDefaultCell().setBorder(Rectangle.NO_BORDER);
            for (int k = 0; k < 24; ++k) {
                if (k != 0)
                    table.addCell("" + k);
                else
                    table.addCell("This is an URL");
            for (int k = 0; k < 24; ++k) {
                if (k != 0)
                    table.addCell("" + k);
                else
                    table.addCell("This is an URL");
            }
            MyTableEvent event = new MyTableEvent();
            table.setTableEvent(event);
            table.setTotalWidth(300);
            // write table 1 at some position
            table.writeSelectedRows(0, -1, 100, 600, writer.getDirectContent());
            // add table 1 (default position)
            document.add(table);
            document.newPage();
            // table 2
            table = new PdfPTable(4);
            float fontSize = 12;
            table.getDefaultCell().setPaddingTop(bf.getFontDescriptor(BaseFont.ASCENT, fontSize) - fontSize + 2);
            table.getDefaultCell().setBorder(Rectangle.NO_BORDER);
            for (int k = 0; k < 500 * 4; ++k) {
                if (k == 0)
                    table.addCell(new Phrase("This is an URL", new Font(bf, fontSize)));
                else
                    table.addCell(new Phrase("" + k, new Font(bf, fontSize)));
            for (int k = 0; k < 500 * 4; ++k) {
                if (k == 0)
                    table.addCell(new Phrase("This is an URL", new Font(bf, fontSize)));
                else
                    table.addCell(new Phrase("" + k, new Font(bf, fontSize)));
            }
            table.setTableEvent(event);
            table.setHeaderRows(3);
            document.add(table);
        }
        catch (Exception de) {
            de.printStackTrace();
        }
        // step 5
        document.close();
    }

