    System.out.println("Chapter 10 example 4

    // step 1
    Document document = new Document();

    try {

        // step 2
        // we create a writer that listens to the document
        // and directs a PDF-stream to a file
        PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("Chap1004.pdf"));

        // step 3
        document.open();

        // step 4
        PdfContentByte cb = writer.getDirectContent();

        // we create a PdfTemplate
        PdfTemplate template = cb.createTemplate(50, 50);
        BaseFont bf = BaseFont.createFont(BaseFont.HELVETICA, BaseFont.CP1252, BaseFont.NOT_EMBEDDED);
        // we add a number of pages
        int i;
        for (i = 1; i < 5; i++) {
            String text = "Page " + writer.getPageNumber() + " of ";
            float len = bf.getWidthPoint(text, 12);
            cb.beginText();
            cb.setFontAndSize(bf, 12);
            cb.setTextMatrix(280, 40);
            cb.showText(text);
            cb.endText();
            cb.addTemplate(template, 280 + len, 40);
            document.newPage();
        for (i = 1; i < 5; i++) {
            String text = "Page " + writer.getPageNumber() + " of ";
            float len = bf.getWidthPoint(text, 12);
            cb.beginText();
            cb.setFontAndSize(bf, 12);
            cb.setTextMatrix(280, 40);
            cb.showText(text);
            cb.endText();
            cb.addTemplate(template, 280 + len, 40);
            document.newPage();
        }
        template.beginText();
        template.setFontAndSize(bf, 12);
        template.showText(String.valueOf(writer.getPageNumber() - 1));
        template.endText();
    }
    catch(DocumentException de) {
        System.out.println(de.getMessage());
    }
    catch(IOException ioe) {
        System.out.println(ioe.getMessage());
    }

    // step 5
    document.close();
}

