private static void deleteNonExecutables(String dir) throws IOException {
	File[] files = new File(dir).listFiles();
	for (int i = 0; i < files.length; i++) 
	{
		File file = files[i];
		if (file.isFile()) 
		{
			String name = file.getName();
			String ext = name.substring(name.lastIndexOf('.')); //get extension
			if (!exeExts.contains(ext)) 
			{
				file.delete();
	for (int i = 0; i < files.length; i++) 
	{
		File file = files[i];
		if (file.isFile()) 
		{
			String name = file.getName();
			String ext = name.substring(name.lastIndexOf('.')); //get extension
			if (!exeExts.contains(ext)) 
			{
				file.delete();
			}
		}
	}
}


public static void Main(String[] args) 
{
	Console.WriteLine("Read field values.");

	PdfReader reader = new PdfReader("AcroForm.pdf");
	PRAcroForm af = reader.getAcroForm();
	ArrayList fields = af.getFields();
	Map map = af.fieldByName;
	Set keys = map.keySet();

	StreamWriter sw = new StreamWriter("AcroFormValues.txt");
	for (Iterator i = keys.iterator(); i.hasNext(); )
	{
		String fieldName = (String)i.next();
		PRAcroForm.FieldInformation info = af.getField(fieldName);
		PdfDictionary dic = info.getInfo();
		String val = dic.contains(PdfName.V) ? 
			PdfReader.getPdfObject(dic.get(PdfName.V)).ToString() 


		sw.WriteLine(fieldName + "=" + val);
	}
	for (Iterator i = keys.iterator(); i.hasNext(); )
	{
		String fieldName = (String)i.next();
		PRAcroForm.FieldInformation info = af.getField(fieldName);
		PdfDictionary dic = info.getInfo();
		String val = dic.contains(PdfName.V) ? 
			PdfReader.getPdfObject(dic.get(PdfName.V)).ToString() 


		sw.WriteLine(fieldName + "=" + val);
	}
	sw.Close();
}


