	Font[] fonts = new Font[14];
	int i = 0;
	fonts[i++] = FontFactory.getFont(FontFactory.COURIER, 12, Font.NORMAL);
	fonts[i++] = FontFactory.getFont(FontFactory.COURIER, 12, Font.BOLD);
	fonts[i++] = FontFactory.getFont(FontFactory.COURIER, 12, Font.ITALIC);
	fonts[i++] = FontFactory.getFont(FontFactory.COURIER, 12, Font.BOLD | Font.ITALIC);
	fonts[i++] = FontFactory.getFont(FontFactory.HELVETICA, 12, Font.NORMAL);
	fonts[i++] = FontFactory.getFont(FontFactory.HELVETICA, 12, Font.BOLD);
	fonts[i++] = FontFactory.getFont(FontFactory.HELVETICA, 12, Font.ITALIC);
	fonts[i++] = FontFactory.getFont(FontFactory.HELVETICA, 12, Font.BOLD | Font.ITALIC);
