/**
 * We only alter the handling of some endtags.
 */

    public void endElement(String uri, String lname, String name) {
        if (myTags.containsKey(name)) {
            XmlPeer peer = (XmlPeer) myTags.get(name);
            // we don't want the document to be close
            // because we are going to add a page after the xml is parsed
            if (isDocumentRoot(peer.getTag())) {
                return;
            }
            handleEndingTags(peer.getTag());
            // we want to add a paragraph after the speaker chunk
            if ("SPEAKER".equals(name)) {
                try {
                    TextElementArray previous = (TextElementArray) stack.pop();
                    previous.add(new Paragraph(16));
                    stack.push(previous);
                }
                catch(EmptyStackException ese) {
                }
            }
        }
        else {
            handleEndingTags(name);
        }
    }


private void AddRadioKid(String caption, String onState, String state, PdfFormField radio, PdfAppearance tpOn, PdfAppearance tpOff)
{
	PdfFormField radio1 = PdfFormField.createEmpty(writer);
	radio1.setFlags(flags);
	radio1.setWidget(new Rectangle(PosX, PosY, PosX + Height, PosY + Height), PdfAnnotation.HIGHLIGHT_INVERT);
	radio1.setAppearanceState(state);
	radio1.setAppearance(PdfAnnotation.APPEARANCE_NORMAL, "Off", tpOff);
	radio1.setAppearance(PdfAnnotation.APPEARANCE_NORMAL, onState, tpOn);
	radio.addKid(radio1);
	cb.beginText();
	cb.setTextMatrix(PosX + Height*2, PosY + (Height-fontSize)/2 + (Height/2)*0.1f);
	cb.showText(caption);
	cb.endText();
}


