	System.out.println("Width");

	// step 1
	Document document = new Document();

	// step 2
	// we create a writer that listens to the document
	PdfWriter.getInstance(document,
		new FileOutputStream("Width.pdf"));

	// step 3
	document.open();
	// step 4
	Chunk c = new Chunk("quick brown fox jumps over the lazy dog");
	float w = c.getWidthPoint();
	Paragraph p = new Paragraph("The width of the chunk
	p.add(c);
	p.add("' is ");
	p.add(String.valueOf(w));
	p.add(" points or ");
	p.add(String.valueOf(w / 72f));
	p.add(" inches.");
	document.add(p);
	document.add(c);
	document.add(Chunk.NEWLINE);
	c.setHorizontalScaling(0.5f);
	document.add(c);
	document.add(c);

	// step 5
	document.close();
}

{

	Console.WriteLine("Chapter 6 example 8

	// step 1
	Document document = new Document();

	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file

	PdfWriter.getInstance(document, new FileStream("Chap0608.pdf", FileMode.Create));

	// step 3
	document.open();

	// step 4
	Image jpg = Image.getInstance("myKids.jpg");
	jpg.setAlignment(Image.MIDDLE);

	jpg.setRotation((float)Math.PI / 6);
	document.add(new Paragraph("rotate 30 degrees"));
	document.add(jpg);
	document.newPage();

	jpg.setRotation((float)Math.PI / 4);
	document.add(new Paragraph("rotate 45 degrees"));
	document.add(jpg);
	document.newPage();

	jpg.setRotation((float)Math.PI / 2);
	document.add(new Paragraph("rotate pi/2 radians"));
	document.add(jpg);
	document.newPage();

	jpg.setRotation((float)(Math.PI * 0.75));
	document.add(new Paragraph("rotate 135 degrees"));
	document.add(jpg);
	document.newPage();

	jpg.setRotation((float)Math.PI);
	document.add(new Paragraph("rotate pi radians"));
	document.add(jpg);
	document.newPage();

	jpg.setRotation((float)(2.0 * Math.PI));
	document.add(new Paragraph("rotate 2 x pi radians"));
	document.add(jpg);

	// step 5
	document.close();
}

	System.out.println("bytes[] / raw image");

	// step 1
	Document document = new Document();

	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file

	PdfWriter.getInstance(document, new FileOutputStream("rawdata.pdf"));

	// step 3
	document.open();

	// step 4

	// creation a jpeg passed as an array of bytes to the Image
	RandomAccessFile rf = new RandomAccessFile("otsoe.jpg", "r");
	int size = (int)rf.length();
	byte imext[] = new byte[size];
	rf.readFully(imext);
	rf.close();
	Image img1 = Image.getInstance(imext);
	img1.setAbsolutePosition(50, 500);
	document.add(img1);

	// creation of an image of 100 x 100 pixels (x 3 bytes for the Red, Green and Blue value)
	byte data[] = new byte[100*100*3];
	for (int k = 0; k < 100; ++k) {
		for (int j = 0; j < 300; j += 3) {
			data[k * 300 + j] = (byte)(255 * Math.sin(j * .5 * Math.PI / 300));
			data[k * 300 + j + 1] = (byte)(256 - j * 256 / 300);
			data[k * 300 + j + 2] = (byte)(255 * Math.cos(k * .5 * Math.PI / 100));
		for (int j = 0; j < 300; j += 3) {
	for (int k = 0; k < 100; ++k) {
		for (int j = 0; j < 300; j += 3) {
			data[k * 300 + j] = (byte)(255 * Math.sin(j * .5 * Math.PI / 300));
			data[k * 300 + j + 1] = (byte)(256 - j * 256 / 300);
			data[k * 300 + j + 2] = (byte)(255 * Math.cos(k * .5 * Math.PI / 100));
		}
	}
	Image img2 = Image.getInstance(100, 100, 3, 8, data);
	img2.setAbsolutePosition(200, 200);
	document.add(img2);

	// step 5
	document.close();
}

{
	Console.WriteLine("Chapter 6 example 9

	// step 1
	Document document = new Document();

	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file

	PdfWriter.getInstance(document, new FileStream("Chap0609.pdf", FileMode.Create));

	// step 3
	document.open();

	// step 4
	// creation a jpeg passed as an array of bytes to the Image
	FileStream fs = File.OpenRead("myKids.jpg");
	int size = (int)fs.Length;
	byte[] imext = new byte[size];
	fs.Read(imext, 0, size);
	fs.Close();
	Image img1 = Image.getInstance(imext);
	img1.setAbsolutePosition(50, 500);
	document.add(img1);

	// creation of an image of 100 x 100 pixels (x 3 bytes for the Red, Green and Blue value)
	byte[] data = new byte[100*100*3];
	for (int k = 0; k < 100; ++k) 
	{
		for (int j = 0; j < 300; j += 3) 
		{
			data[k * 300 + j] = (byte)(255 * Math.Sin(j * .5 * Math.PI / 300));
			data[k * 300 + j + 1] = (byte)(256 - j * 256 / 300);
			data[k * 300 + j + 2] = (byte)(255 * Math.Cos(k * .5 * Math.PI / 100));
		}
		for (int j = 0; j < 300; j += 3) 
		{
			data[k * 300 + j] = (byte)(255 * Math.Sin(j * .5 * Math.PI / 300));
			data[k * 300 + j + 1] = (byte)(256 - j * 256 / 300);
			data[k * 300 + j + 2] = (byte)(255 * Math.Cos(k * .5 * Math.PI / 100));
		}
	}
	for (int k = 0; k < 100; ++k) 
	{
		for (int j = 0; j < 300; j += 3) 
		{
			data[k * 300 + j] = (byte)(255 * Math.Sin(j * .5 * Math.PI / 300));
			data[k * 300 + j + 1] = (byte)(256 - j * 256 / 300);
			data[k * 300 + j + 2] = (byte)(255 * Math.Cos(k * .5 * Math.PI / 100));
		}
	}
	Image img2 = Image.getInstance(100, 100, 3, 8, data);
	img2.setAbsolutePosition(200, 200);
	document.add(img2);

	// step 5
	document.close();
}
