{
	Console.WriteLine("Chapter 1 example 11

	// step 1
	Document document = new Document();

	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter writerA = PdfWriter.getInstance(document, new FileStream("Chap0111a.pdf", FileMode.Create));
	PdfWriter writerB = PdfWriter.getInstance(document, new FileStream("Chap0111b.pdf", FileMode.Create));

	// step 3
	// we add a Watermark that will show up on PAGE 1
	writerB.pause();
	try 
	{
		Watermark watermark = new Watermark(Image.getInstance("watermark.jpg"), 200, 420);
		document.add(watermark);
	}
	catch(Exception e) 
	{
		Console.WriteLine("Are you sure you have the file 'watermark.jpg' in the right path?");
	}
	writerB.resume();

	// we add a Header that will show up on PAGE 1
	HeaderFooter header = new HeaderFooter(new Phrase("This is a header"), false);
	document.setHeader(header);

	// we open the document
	document.open();

	// we rotate the page, starting from PAGE 2
	document.setPageSize(PageSize.A4.rotate());

	// we need to change the position of the Watermark
	try 
	{
		Watermark watermark = new Watermark(Image.getInstance("watermark.jpg"), 320, 200);
		document.add(watermark);
	}
	catch(Exception e) 
	{
		Console.WriteLine("Are you sure you have the file 'watermark.jpg' in the right path?");
	}

	// we add a Footer that will show up on PAGE 2
	HeaderFooter footer = new HeaderFooter(new Phrase("This is page
	document.setFooter(footer);

	// step 4

	// PAGE 1
	document.add(new Paragraph("Hello World"));

	// we trigger a page break
	document.newPage();

	// PAGE 2
	// we add some more content
	document.add(new Paragraph("Hello Earth"));

	// we remove the header starting from PAGE 3
	writerA.pause();
	document.resetHeader();
	writerA.resume();

	// we trigger a page break
	document.newPage();

	// PAGE 3
	// we add some more content
	document.add(new Paragraph("Hello Sun"));
	writerA.pause();
	document.add(new Paragraph("Remark
	writerA.resume();

	// we reset the page numbering
	writerB.pause();
	document.resetPageCount();
	writerB.resume();

	// we trigger a page break
	document.newPage();

	// PAGE 4
	// we add some more content
	document.add(new Paragraph("Hello Moon"));
	writerB.pause();
	document.add(new Paragraph("Remark
	writerB.resume();

	// step 5
	document.close();
}

{
	Console.WriteLine("Chapter 1 example 12

	// we create a reader for a certain document
	PdfReader reader = new PdfReader("Chap0703.pdf");
	// we retrieve the total number of pages
	int n = reader.getNumberOfPages();
	// we retrieve the size of the first page
	Rectangle psize = reader.getPageSize(1);
	float width = psize.width();
	float height = psize.height();

	// step 1
	Document document = new Document(psize, 50, 50, 50, 50);
	// step 2
	PdfWriter writer = PdfWriter.getInstance(document, new FileStream("Chap0112.pdf", FileMode.Create));
	// step 3
	try 
	{
		Watermark watermark = new Watermark(Image.getInstance("watermark.jpg"), 200, 320);
		document.add(watermark);
	}
	catch(Exception e) 
	{
		Console.WriteLine("Are you sure you have the file 'watermark.jpg' in the right path?");
	}
	document.open();
	// step 4
	PdfContentByte cb = writer.getDirectContent();
	int i = 0;
	int p = 0;
	Console.WriteLine("There are " + n + " pages in the document.");
	while (i < n) 
	{
		document.newPage();
		p++;
		i++;
		PdfImportedPage page1 = writer.getImportedPage(reader, i);
		cb.addTemplate(page1, .5f, 0, 0, .5f, 0, height / 2);
		Console.WriteLine("processed page " + i);
		if (i < n) 
		{
			i++;
			PdfImportedPage page2 = writer.getImportedPage(reader, i);
			cb.addTemplate(page2, .5f, 0, 0, .5f, width / 2, height / 2);
			Console.WriteLine("processed page " + i);
		}
		if (i < n) 
		{
			i++;
			PdfImportedPage page3 = writer.getImportedPage(reader, i);
			cb.addTemplate(page3, .5f, 0, 0, .5f, 0, 0);
			Console.WriteLine("processed page " + i);
		}
		if (i < n) 
		{
			i++;
			PdfImportedPage page4 = writer.getImportedPage(reader, i);
			cb.addTemplate(page4, .5f, 0, 0, .5f, width / 2, 0);
			Console.WriteLine("processed page " + i);
		}
		cb.setRGBColorStroke(255, 0, 0);
		cb.moveTo(0, height / 2);
		cb.lineTo(width, height / 2);
		cb.stroke();
		cb.moveTo(width / 2, height);
		cb.lineTo(width / 2, 0);
		cb.stroke();
		BaseFont bf = BaseFont.createFont(BaseFont.HELVETICA, BaseFont.CP1252, BaseFont.NOT_EMBEDDED);
		cb.beginText();
		cb.setFontAndSize(bf, 14);
		cb.showTextAligned(PdfContentByte.ALIGN_CENTER, "page " + p + " of " + ((n / 4) + (n % 4 > 0? 1 
		cb.endText();
	}
	while (i < n) 
	{
		document.newPage();
		p++;
		i++;
		PdfImportedPage page1 = writer.getImportedPage(reader, i);
		cb.addTemplate(page1, .5f, 0, 0, .5f, 0, height / 2);
		Console.WriteLine("processed page " + i);
		if (i < n) 
		{
			i++;
			PdfImportedPage page2 = writer.getImportedPage(reader, i);
			cb.addTemplate(page2, .5f, 0, 0, .5f, width / 2, height / 2);
			Console.WriteLine("processed page " + i);
		}
		if (i < n) 
		{
			i++;
			PdfImportedPage page3 = writer.getImportedPage(reader, i);
			cb.addTemplate(page3, .5f, 0, 0, .5f, 0, 0);
			Console.WriteLine("processed page " + i);
		}
		if (i < n) 
		{
			i++;
			PdfImportedPage page4 = writer.getImportedPage(reader, i);
			cb.addTemplate(page4, .5f, 0, 0, .5f, width / 2, 0);
			Console.WriteLine("processed page " + i);
		}
		cb.setRGBColorStroke(255, 0, 0);
		cb.moveTo(0, height / 2);
		cb.lineTo(width, height / 2);
		cb.stroke();
		cb.moveTo(width / 2, height);
		cb.lineTo(width / 2, 0);
		cb.stroke();
		BaseFont bf = BaseFont.createFont(BaseFont.HELVETICA, BaseFont.CP1252, BaseFont.NOT_EMBEDDED);
		cb.beginText();
		cb.setFontAndSize(bf, 14);
		cb.showTextAligned(PdfContentByte.ALIGN_CENTER, "page " + p + " of " + ((n / 4) + (n % 4 > 0? 1 
		cb.endText();
	}
	// step 5
	document.close();
