        ct.setSimpleColumn(ph, 200 + 2 * gap, 0, 200 + 2 * gap + 200, 500 - 200 - gap, 18, ElementConst.ALIGN_CENTER);
        ct.go();

        document.newPage();
        String text = "text ";
        text += text;
