        text += text;
        document.add(new Paragraph(text));
        Image img = Image.getInstance("vonnegut.gif");
        img.setAbsolutePosition(100, 600);
        SByte[] gradient = new SByte[256];
        for (int k = 0; k < 256; ++k)
            gradient[k] = (SByte)k;
        for (int k = 0; k < 256; ++k)
            gradient[k] = (SByte)k;
        Image smask = Image.getInstance(256, 1, 1, 8, gradient);
