private static void copyFiles(string[] filenames, String destDir) 
{
	foreach (string filename in filenames)
	{
		System.IO.File.Copy(filename, 
			Path.Combine(destDir, 
			Path.GetFileName(filename)), true);
	}
	foreach (string filename in filenames)
	{
		System.IO.File.Copy(filename, 
			Path.Combine(destDir, 
			Path.GetFileName(filename)), true);
	}
}

