	System.out.println("JavaScript");

	// step 1
	Document document = new Document();
	// step 2
	PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream(
		"JavaScriptAction.pdf"));
	// step 3
	document.open();
	// step 4
	Paragraph p = new Paragraph(new Chunk("Click to say Hello")
		.setAction(PdfAction.javaScript("app.alert('Hello');\r", writer)));
	document.add(p);
	// step 5
	document.close();
}

	System.out.println("Chained actions");

	// step 1
	Document document = new Document();
	// step 2
	PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream(
		"ChainedActions.pdf"));
	// step 3
	document.open();
	// step 4
	PdfAction action = PdfAction.javaScript("app.alert('Welcome at my site');\r", writer);
	action.next(new PdfAction("http
	Paragraph p = new Paragraph(new Chunk("Click to go to Bruno's site")
		.setAction(action));
	document.add(p);
	// step 5
	document.close();

}


/**
 * @see com.lowagie.text.SplitCharacter#isSplitCharacter(int, int, int, char[], com.lowagie.text.pdf.PdfChunk[])
 */
public boolean isSplitCharacter(int start, int current, int end, char[] cc, PdfChunk[] ck) {
	char c;
	if (ck == null)
		c = cc[current];
	else
		c = ck[Math.min(current, ck.length - 1)].getUnicodeEquivalent(cc[current]);
	return (c == '.');
}


public boolean isSplitCharacter(int start, int current, int end, char[] cc, PdfChunk[] ck) {
    char c;
    if (ck == null)
        c = cc[current];
    else
        c = ck[Math.min(current, ck.length - 1)].getUnicodeEquivalent(cc[current]);
    return (c == '.');
}


public int[] readInts(int length)  throws IOException 
{
	int[] a = new int[length];
	for (int i = 0; i < length; i++)
	{
		a[i] = readInt();
	for (int i = 0; i < length; i++)
	{
		a[i] = readInt();
	}
	return a;
}


public long[] readUnsignedInts(int length)  throws IOException 
{
	long[] a = new long[length];
	for (int i = 0; i < length; i++)
	{
		a[i] = readUnsignedInt();
	for (int i = 0; i < length; i++)
	{
		a[i] = readUnsignedInt();
	}
	return a;
}

