	System.out.println("Transformations");        
	// step 1
	Document document = new Document(PageSize.A4);

	// step 2
	PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("transformations.pdf"));

	// step 3
	document.open();

	// step 4
	PdfContentByte cb = writer.getDirectContent();

	// we create a PdfTemplate
	PdfTemplate template = cb.createTemplate(120, 120);

	// we add some graphics
	template.moveTo(30, 10);
	template.lineTo(90, 10);
	template.lineTo(90, 80);
	template.lineTo(110, 80);
	template.lineTo(60, 110);
	template.lineTo(10, 80);
	template.lineTo(30, 80);
	template.closePath();
	template.stroke();

	// we add the template on different positions
	cb.addTemplate(template, 0, 0);
	cb.addTemplate(template, 0, 1, -1, 0, 200, 600);
	cb.addTemplate(template, .5f, 0, 0, .5f, 100, 400);

	// we go to a new page
	document.newPage();
	cb.addTemplate(template, 0, 500);
	cb.addTemplate(template, 2, 0, -1, 2, 200, 300);

	// step 5
	document.close();
}

	System.out.println("Text at absolute positions");

	// step 1
	Document document = new Document();

	// step 2
	PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("complextext.pdf"));

	// step 3
	document.open();

	// step 4
	PdfContentByte cb = writer.getDirectContent();

	// first we draw some lines to be able to visualize the text alignment functions
	cb.setLineWidth(0f);
	cb.moveTo(250, 500);
	cb.lineTo(250, 800);
	cb.moveTo(50, 700);
	cb.lineTo(400, 700);
	cb.moveTo(50, 650);
	cb.lineTo(400, 650);
	cb.moveTo(50, 600);
	cb.lineTo(400, 600);
	cb.stroke();

	// we construct a font
	java.io.File fontFile = new java.io.File(Configuration.fontsDirectory + File.separator + "arialuni.ttf");
	if (!fontFile.exists()) {
		// Microsoft has removed the Arial Unicode MS Font (arialuni.ttf) for Publisher 2000 download.
		fontFile = new java.io.File(Configuration.fontsDirectory + File.separator + "times.ttf");
	}
	BaseFont bf = BaseFont.createFont(fontFile.getCanonicalPath(), BaseFont.IDENTITY_H, true);
	Font ft = new Font(bf, 12);
	// This is the text
	String text = "\u0623\u0648\u0631\u0648\u0628\u0627, \u0628\u0631\u0645\u062c\u064a\u0627\u062a \u0627\u0644\u062d\u0627\u0633\u0648\u0628 + \u0627\u0646\u062a\u0631\u0646\u064a\u062a 
	Phrase center = new Phrase(text + " Center", ft);
	ColumnText.showTextAligned(cb, PdfContentByte.ALIGN_CENTER, center, 250, 700, 0, PdfWriter.RUN_DIRECTION_RTL, 0);
	ColumnText.showTextAligned(cb, PdfContentByte.ALIGN_RIGHT, new Phrase(text + " Right", ft), 250, 650, 20, PdfWriter.RUN_DIRECTION_RTL, 0);
	ColumnText.showTextAligned(cb, PdfContentByte.ALIGN_LEFT, new Phrase("Some text Left aligned", ft), 250, 600, 20);
	float size = ColumnText.getWidth(center, PdfWriter.RUN_DIRECTION_RTL, 0);
	cb.setRGBColorStroke(255, 0, 0);
	cb.rectangle(250 - size/2, 690, size, 30);
	cb.stroke();

	// step 5
	document.close();
}

