	System.out.println("Add watermarks and pagenumbers");
	// we create a reader for a certain document
	PdfReader reader = new PdfReader("ChapterSection.pdf");
	int n = reader.getNumberOfPages();
	// we create a stamper that will copy the document to a new file
	PdfStamper stamp = new PdfStamper(reader, new FileOutputStream("watermark_pagenumbers.pdf"));
	// adding some metadata
	HashMap moreInfo = new HashMap();
	moreInfo.put("Author", "Bruno Lowagie");
	stamp.setMoreInfo(moreInfo);
	// adding content to each page
	int i = 0;
	PdfContentByte under;
	PdfContentByte over;
	Image img = Image.getInstance("watermark.jpg");
	BaseFont bf = BaseFont.createFont(BaseFont.HELVETICA, BaseFont.WINANSI, BaseFont.EMBEDDED);
	img.setAbsolutePosition(200, 400);
	while (i < n) {
		i++;
		// watermark under the existing page
		under = stamp.getUnderContent(i);
		under.addImage(img);
		// text over the existing page
		over = stamp.getOverContent(i);
		over.beginText();
		over.setFontAndSize(bf, 18);
		over.setTextMatrix(30, 30);
		over.showText("page " + i);
		over.setFontAndSize(bf, 32);
		over.showTextAligned(Element.ALIGN_LEFT, "DUPLICATE", 230, 430, 45);
		over.endText();
	while (i < n) {
		i++;
		// watermark under the existing page
		under = stamp.getUnderContent(i);
		under.addImage(img);
		// text over the existing page
		over = stamp.getOverContent(i);
		over.beginText();
		over.setFontAndSize(bf, 18);
		over.setTextMatrix(30, 30);
		over.showText("page " + i);
		over.setFontAndSize(bf, 32);
		over.showTextAligned(Element.ALIGN_LEFT, "DUPLICATE", 230, 430, 45);
		over.endText();
	}
	// adding an extra page
	stamp.insertPage(1, PageSize.A4);
	over = stamp.getOverContent(1);
	over.beginText();
	over.setFontAndSize(bf, 18);
	over.showTextAligned(Element.ALIGN_LEFT, "DUPLICATE OF AN EXISTING PDF DOCUMENT", 30, 600, 0);
	over.endText();
	// adding a page from another document
	PdfReader reader2 = new PdfReader("SimpleAnnotations1.pdf");
	under = stamp.getUnderContent(1);
	under.addTemplate(stamp.getImportedPage(reader2, 3), 1, 0, 0, 1, 0, 0);
	// closing PdfStamper will generate the new PDF file
	stamp.close();
}


/**
 * General example using table events (with colspan).
 * @param args
 * 		no arguments needed
 */
public static void main(String[] args) throws Exception {

	System.out.println("Table Events 2");
	// step1
	Document document = new Document(PageSize.A4, 50, 50, 50, 50);
	// step2
	PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("TableEvents2.pdf"));
	// step3
	document.open();
	// step4
	PdfPTable table = new PdfPTable(4);
	table.getDefaultCell().setBorder(Rectangle.NO_BORDER);
	for (int k = 0; k < 24; ++k) {
		if (k != 0)
			table.addCell(String.valueOf(k));
		else {
			table.getDefaultCell().setColspan(3);
			table.getDefaultCell().setHorizontalAlignment(Element.ALIGN_CENTER);
			table.addCell("This is a very big URL");
			table.getDefaultCell().setColspan(1);
			table.getDefaultCell().setHorizontalAlignment(Element.ALIGN_LEFT);
			k += 2;
	for (int k = 0; k < 24; ++k) {
		if (k != 0)
			table.addCell(String.valueOf(k));
		else {
			table.getDefaultCell().setColspan(3);
			table.getDefaultCell().setHorizontalAlignment(Element.ALIGN_CENTER);
			table.addCell("This is a very big URL");
			table.getDefaultCell().setColspan(1);
			table.getDefaultCell().setHorizontalAlignment(Element.ALIGN_LEFT);
			k += 2;
		}
	}
	TableEvents2 event = new TableEvents2();
	table.setTableEvent(event);

	// add the table with document add
	document.add(table);
	// add the table at an absolute position
	table.setTotalWidth(300);
	table.writeSelectedRows(0, -1, 100, 600, writer.getDirectContent());
	document.newPage();

	table = new PdfPTable(4);
	float fontSize = 12;
	BaseFont bf = BaseFont.createFont("Helvetica", "winansi", false);
	table.getDefaultCell().setPaddingTop(bf.getFontDescriptor(BaseFont.ASCENT, fontSize) - fontSize + 2);
	table.getDefaultCell().setBorder(Rectangle.NO_BORDER);
	for (int k = 0; k < 500 * 4; ++k) {
		if (k == 0) {
			table.getDefaultCell().setColspan(4);
			table.getDefaultCell().setHorizontalAlignment(Element.ALIGN_CENTER);
			table.addCell(new Phrase("This is an URL", new Font(bf, fontSize * 2)));
			table.getDefaultCell().setColspan(1);
			table.getDefaultCell().setHorizontalAlignment(Element.ALIGN_LEFT);
			k += 3;
		}
		else
			table.addCell(new Phrase(String.valueOf(k), new Font(bf, fontSize)));
	for (int k = 0; k < 500 * 4; ++k) {
		if (k == 0) {
			table.getDefaultCell().setColspan(4);
			table.getDefaultCell().setHorizontalAlignment(Element.ALIGN_CENTER);
			table.addCell(new Phrase("This is an URL", new Font(bf, fontSize * 2)));
			table.getDefaultCell().setColspan(1);
			table.getDefaultCell().setHorizontalAlignment(Element.ALIGN_LEFT);
			k += 3;
		}
		else
			table.addCell(new Phrase(String.valueOf(k), new Font(bf, fontSize)));
	}
	table.setTableEvent(event);
	table.setHeaderRows(3);
	document.add(table);
	// step5
	document.close();
}

	System.out.println("Add watermarks and pagenumbers");
