    System.out.println("Nested tables");
    // step 1
    Document document = new Document();
    // step 2
    PdfWriter.getInstance(document, new FileOutputStream("nestedtablesa.pdf"));
    // step 3
    document.open();
    // step 4

    // example 1

    Table secondTable = new Table(2);
    secondTable.addCell("2nd table 0.0");
    secondTable.addCell("2nd table 0.1");
    secondTable.addCell("2nd table 1.0");
    secondTable.addCell("2nd table 1.1");

    Table aTable = new Table(4,4);    // 4 rows, 4 columns
    aTable.setAutoFillEmptyCells(true);
    aTable.addCell("2.2", new Point(2,2));
    aTable.addCell("3.3", new Point(3,3));
    aTable.addCell("2.1", new Point(2,1));
    aTable.insertTable(secondTable, new Point(1,3));
    document.add(aTable);
    document.add(new Paragraph("converted to PdfPTable
    aTable.setConvert2pdfptable(true);
    document.add(aTable);
    document.newPage();

    // example 2

    Table thirdTable = new Table(2);
    thirdTable.addCell("3rd table 0.0");
    thirdTable.addCell("3rd table 0.1");
    thirdTable.addCell("3rd table 1.0");
    thirdTable.addCell("3rd table 1.1");

    aTable = new Table(5,5);
    aTable.setAutoFillEmptyCells(true);
    aTable.addCell("2.2", new Point(2,2));
    aTable.addCell("3.3", new Point(3,3));
    aTable.addCell("2.1", new Point(2,1));
    aTable.insertTable(secondTable, new Point(1,3));
    aTable.insertTable(thirdTable, new Point(6,2));
    document.add(aTable); 
    document.add(new Paragraph("converted to PdfPTable
    aTable.setConvert2pdfptable(true);
    document.add(aTable);     
    document.newPage();  

    // example 3
    aTable = new Table(3);
    float[] widths = {1, 2, 1};
    aTable.setWidths(widths);
    aTable.addCell("1.1");
    aTable.addCell("1.2");
    aTable.addCell("1.3");
    // nested
    Table t2 = new Table(2);
    t2.addCell("2.1");
    t2.addCell("2.2");

    // now insert the nested
    aTable.insertTable(t2);
    aTable.addCell("new cell");
    document.add(aTable);    
    document.add(new Paragraph("converted to PdfPTable
    aTable.setConvert2pdfptable(true);
    document.add(aTable);
    document.newPage();

    // relative column widths are preserved

    Table a = new Table( 2 );
    a.setWidths( new float[] { 85, 15 } );
    a.addCell("a-1");
    a.addCell("a-2");

    Table b = new Table(5);
    b.setWidths( new float[] { 15, 7, 7, 7, 7 } );
    b.addCell("b-1");
    b.addCell("b-2");
    b.addCell("b-3");
    b.addCell("b-4");
    b.addCell("b-5");

    // now, insert these 2 tables into a third for layout purposes
    Table c = new Table( 3, 1 );
    c.setWidth( 100.0f );
    c.setWidths( new float[] { 20, 2, 78 } );
    c.insertTable(a, new Point(0,0) );
    c.insertTable(b, new Point(0,2) );

    document.add(c);
    document.add(new Paragraph("converted to PdfPTable
    c.setConvert2pdfptable(true);
    document.add(c);

    // step 5
    document.close();
}

{
	float[] widths = width[0];
	PdfContentByte cb = canvases[PdfPTable.TEXTCANVAS];
	cb.saveState();
	cb.setLineWidth(2);
	cb.setRGBColorStroke(255, 0, 0);
	cb.rectangle(widths[0], heights[heights.Length - 1], widths[widths.Length - 1] - widths[0], heights[0] - heights[heights.Length - 1]);
	cb.stroke();
	if (headerRows > 0) 
	{
		float headerHeight = heights[0];
		for (int k = 0; k < headerRows; ++k)
			headerHeight += heights[k];
		for (int k = 0; k < headerRows; ++k)
			headerHeight += heights[k];
		cb.setRGBColorStroke(0, 0, 255);
		cb.rectangle(widths[0], heights[headerRows], widths[widths.Length - 1] - widths[0], heights[0] - heights[headerRows]);
		cb.stroke();
	}
	cb.restoreState();
	cb = canvases[PdfPTable.BASECANVAS];
	cb.saveState();
	cb.setLineWidth(.5f);
	for (int line = 0; line < heights.Length - 1; ++line) 
	{

		widths = width[line];
		for (int col = 0; col < widths.Length - 1; ++col) 
		{
			if (line == 0 && col == 0)
				cb.setAction(new PdfAction("http
					widths[col], heights[line + 1], widths[col + 1], heights[line]);
			cb.setRGBColorStrokeF((float)rnd.NextDouble(), (float)rnd.NextDouble(), (float)rnd.NextDouble());
			cb.moveTo(widths[col], heights[line]);
			cb.lineTo(widths[col + 1], heights[line]);
			cb.stroke();
			cb.setRGBColorStrokeF((float)rnd.NextDouble(), (float)rnd.NextDouble(), (float)rnd.NextDouble());
			cb.moveTo(widths[col], heights[line]);
			cb.lineTo(widths[col], heights[line + 1]);
			cb.stroke();
		}
		for (int col = 0; col < widths.Length - 1; ++col) 
		{
			if (line == 0 && col == 0)
				cb.setAction(new PdfAction("http
					widths[col], heights[line + 1], widths[col + 1], heights[line]);
			cb.setRGBColorStrokeF((float)rnd.NextDouble(), (float)rnd.NextDouble(), (float)rnd.NextDouble());
			cb.moveTo(widths[col], heights[line]);
			cb.lineTo(widths[col + 1], heights[line]);
			cb.stroke();
			cb.setRGBColorStrokeF((float)rnd.NextDouble(), (float)rnd.NextDouble(), (float)rnd.NextDouble());
			cb.moveTo(widths[col], heights[line]);
			cb.lineTo(widths[col], heights[line + 1]);
			cb.stroke();
		}
	}
	for (int line = 0; line < heights.Length - 1; ++line) 
	{

		widths = width[line];
		for (int col = 0; col < widths.Length - 1; ++col) 
		{
			if (line == 0 && col == 0)
				cb.setAction(new PdfAction("http
					widths[col], heights[line + 1], widths[col + 1], heights[line]);
			cb.setRGBColorStrokeF((float)rnd.NextDouble(), (float)rnd.NextDouble(), (float)rnd.NextDouble());
			cb.moveTo(widths[col], heights[line]);
			cb.lineTo(widths[col + 1], heights[line]);
			cb.stroke();
			cb.setRGBColorStrokeF((float)rnd.NextDouble(), (float)rnd.NextDouble(), (float)rnd.NextDouble());
			cb.moveTo(widths[col], heights[line]);
			cb.lineTo(widths[col], heights[line + 1]);
			cb.stroke();
		}
	}
	cb.restoreState();
}

