class MyPageEvents extends PdfPageEventHelper {

    // we will keep a list of speakers
    TreeSet speakers = new TreeSet();

    // This is the contentbyte object of the writer
    PdfContentByte cb;

    // we will put the final number of pages in a template
    PdfTemplate template;

    // this is the BaseFont we are going to use for the header / footer
    BaseFont bf = null;

    // this is the current act of the play
    String act = "";


// ?t?B?[???h
public static final int TIFF_BYTE = 1;
public static final int TIFF_ASCII = 2;
public static final int TIFF_SHORT = 3;
public static final int TIFF_LONG = 4;
public static final int TIFF_RATIONAL = 5;
public static final int TIFF_SBYTE = 6;
public static final int TIFF_UNDEFINED = 7;
public static final int TIFF_SSHORT = 8;
public static final int TIFF_SLONG = 9;
public static final int TIFF_SRATIONAL = 10;
public static final int TIFF_FLOAT = 11;
public static final int TIFF_DOUBLE = 12;

int tag;
int type;
int count;
TIFFValueProxy data;

public TIFFField(int tag, int type, int count, TIFFValueProxy data)
{
	this.tag = tag;
	this.type = type;
	this.count = count;
	this.data = data;
}

class MyPageEvents extends PdfPageEventHelper {

    // we will keep a list of speakers
    TreeSet speakers = new TreeSet();

    // This is the contentbyte object of the writer
    PdfContentByte cb;

    // we will put the final number of pages in a template
    PdfTemplate template;

    // this is the BaseFont we are going to use for the header / footer
    BaseFont bf = null;

    // this is the current act of the play
    String act = "";

public TIFFValueProxy(StreamReader s, long pos, int size, boolean isBigEndian) 
{
	this.stream = s;
	this.position = pos;
	this.size = size;
	this.isBigEndian = isBigEndian;
}


public long readUnsignedInt() throws IOException
{
	long s = readInt();
	if (s < 0) s += 0x100000000L;
	return s;
}


public short readUnsignedByte() throws IOException
{
	short b = this.readByte();
	if (b < 0) b += 0x100;
	return b;
}

public static long ToUnsigned(int v)
{
	int s = v;
	if (v < 0) {
		s += 4294967296l;
	}
	return s;
}

public static int ToUnsigned(short v)
{
	int s = v;
	if (v < 0) {
		s += 65536;
	}
	return s;
}

public static long ToUnsigned(int v)
{
	int s = v;
	if (v < 0) {
		s += 4294967296l;
	}
	return s;
}


public int readUnsignedShort() throws IOException
{
	int s = readShort();
	if (s < 0) s += 0x10000;
	return s;
}

	System.out.println("Hello World");

	// step 1
	Document document = new Document();

	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter.getInstance(document,
		new FileOutputStream("HelloWorld.pdf"));

	// step 3
	document.open();
	// step 4
	document.add(new Paragraph("Hello World"));

	// step 5
	document.close();
}

