	System.out.println("True Type fonts (not embedded)");

	// step 1
	Document document = new Document();

	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter.getInstance(document, new FileOutputStream("truetype.pdf"));

	// step 3
	document.open();

	// step 4
	BaseFont bfComic = BaseFont.createFont(Configuration.fontsDirectory + File.separator + "comicbd.ttf", BaseFont.CP1252, BaseFont.NOT_EMBEDDED);
	Font font = new Font(bfComic, 12);
	String text1 = "This is the quite popular True Type font 'Comic'.";
	document.add(new Paragraph(text1, font));

	// step 5
	document.close();
}

