/**
 * Example originally written by Wendy Smoak to generate a Table with
 * 'floating boxes'. Adapted by Bruno Lowagie.
 * 
 * @param args
 */
public static void main(String[] args) throws Exception {
	FloatingBoxes floatingBoxes = new FloatingBoxes();
	// step 1
	Document document = new Document();
	// step 2
	PdfWriter.getInstance(document, new FileOutputStream(
		"FloatingBoxes.pdf"));
	// step 3
	document.open();
	// step 4
	PdfPTable table = new PdfPTable(2);
	table.setTableEvent(floatingBoxes);
	table.getDefaultCell().setBorder(Rectangle.NO_BORDER);
	table.getDefaultCell().setCellEvent(floatingBoxes);
	table.getDefaultCell().setPadding(5f);
	table.addCell("value");
	table.addCell("name");
	table.addCell(new Paragraph("dog"));
	table.addCell(new Paragraph("cat"));
	table.addCell(new Paragraph("bird"));
	table.addCell(new Paragraph("horse"));
	document.add(table);
	// step 5
	document.close();
}

	System.out.println("Hello World");

	// step 1
	Document document = new Document();

	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter.getInstance(document,
		new FileOutputStream("HelloWorld.pdf"));

	// step 3
	document.open();
	// step 4
	document.add(new Paragraph("Hello World"));

	// step 5
	document.close();
}

	System.out.println("Open Application");

	// step 1
	Document document = new Document();

	// step 2
	HtmlWriter.getInstance(document, new FileOutputStream(
		"JavaScriptAction.html"));
	// step 3

	StringBuffer javaScriptSection = new StringBuffer();
	javaScriptSection.append("\t\tfunction load() {\n");
	javaScriptSection.append("\t\t  alert('Page has been loaded.');\n");
	javaScriptSection.append("\t\t}\n");

	javaScriptSection.append("\t\tfunction unload(){\n");
	javaScriptSection.append("\t\t  alert('Page has been unloaded.');\n");
	javaScriptSection.append("\t\t}\n");

	javaScriptSection.append("\t\tfunction sayHi(){\n");
	javaScriptSection.append("\t\t  alert('Hi !!!');\n");
	javaScriptSection.append("\t\t}");

	document.add(new Header(HtmlTags.JAVASCRIPT, javaScriptSection.toString()));
	document.setJavaScript_onLoad  ("load()");
	document.setJavaScript_onUnLoad("unload()");

	document.open();
	// step 4
	Phrase phrase1 = new Phrase("There are 3 JavaScript functions in the HTML page, load(), unload() and sayHi().\n\n" +
		"The first one will be called when the HTML page has been loaded by your browser.\n" +
		"The second one will be called when the HTML page is being unloaded,\n" +
		"for example when you go to another page.\n");
	document.add(phrase1);

	// add a HTML link <A HREF="...">
	Anchor anchor = new Anchor("Click here to execute the third JavaScript function.");
	anchor.setReference("JavaScript
	document.add(anchor);

	// step 5
	document.close();

}

	System.out.println("Hello World");

	// step 1
	Document document = new Document();

	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter.getInstance(document,
		new FileOutputStream("HelloWorld.pdf"));

	// step 3
	document.open();
	// step 4
	document.add(new Paragraph("Hello World"));

	// step 5
	document.close();
}

	System.out.println("My First PdfPTable");

	// step 1
	Document document = new Document();

	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter.getInstance(document, new FileOutputStream("MyFirstTable.pdf"));

	// step 3
	document.open();

	PdfPTable table = new PdfPTable(3);
	PdfPCell cell = new PdfPCell(new Paragraph("header with colspan 3"));
	cell.setColspan(3);
	table.addCell(cell);
	table.addCell("1.1");
	table.addCell("2.1");
	table.addCell("3.1");
	table.addCell("1.2");
	table.addCell("2.2");
	table.addCell("3.2");
	cell = new PdfPCell(new Paragraph("cell test1"));
	cell.setBorderColor(new Color(255, 0, 0));
	table.addCell(cell);
	cell = new PdfPCell(new Paragraph("cell test2"));
	cell.setColspan(2);
	cell.setBackgroundColor(new Color(0xC0, 0xC0, 0xC0));
	table.addCell(cell);
	document.add(table);

	// step 5
	document.close();
}

	System.out.println("Hello World");

	// step 1
	Document document = new Document();

	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
