	BufferedWriter out = new BufferedWriter(new FileWriter("fullfontname_arialbi.txt"));
	BaseFont bf = BaseFont.createFont(Configuration.fontsDirectory + File.separator + "arialbi.ttf", "winansi", BaseFont.NOT_EMBEDDED);
	out.write("postscriptname
	out.write("\r\n\r\n");
	String names[][] = bf.getFullFontName();
	out.write("\n\nListing the full font name
	for (int k = 0; k < names.length; ++k) {
		if (names[k][0].equals("3") && names[k][1].equals("1")) // Microsoft encoding
			out.write(names[k][3] + "\r\n");
	for (int k = 0; k < names.length; ++k) {
		if (names[k][0].equals("3") && names[k][1].equals("1")) // Microsoft encoding
			out.write(names[k][3] + "\r\n");
	}
	out.flush();
	out.close();
}

