        document.add(table);
        document.add(new Paragraph("blah blah."));
        document.add(table);
        document.add(new Paragraph("A negative offset."));
        table.setOffset(-16);
        document.add(table);
        document.add(new Paragraph("blah blah."));
        document.add(table);
    }
    catch(DocumentException de) {
