public long[] getAsRational(int index) throws IOException 
{
	data.stream.seek(data.position + sizeOfType(type) * index);
	return data.stream.readUnsignedInts(2);
}

public byte[] getAsBytes() throws IOException
{ 
	data.stream().seek(data.position);
	byte[] a = new byte[data.size];
	data.stream.read(a);
	return a;
}


