private static PdfPCell makeCell(String text, int vAlignment, int hAlignment, Font font, float leading, float padding, Rectangle borders, boolean ascender, boolean descender) {
	Paragraph p = new Paragraph(text, font);
	p.setLeading(leading);

	PdfPCell cell = new PdfPCell(p);
	cell.setLeading(leading, 0);
	cell.setVerticalAlignment(vAlignment);
	cell.setHorizontalAlignment(hAlignment);
	cell.cloneNonPositionParameters(borders);
	cell.setUseAscender(ascender);
	cell.setUseDescender(descender);
	cell.setUseBorderPadding(true);
	cell.setPadding(padding);
	return cell;
}


private static Element newPara(String text, int alignment, int type) {
	Font font = FontFactory.getFont("Helvetica", 10, type, Color.BLACK);
	Paragraph p = new Paragraph(text, font);
	p.setAlignment(alignment);
	p.setLeading(font.size() * 1.2f);
	return p;
}


    static void stampSignature(String filename, String signedFilename, 
        String keyFilename, String password, 
        int signType, String fieldName, boolean append, 
        Rectangle rect, int page,
        String reason, String location) throws Exception {
        KeyStore ks = KeyStore.getInstance("pkcs12");
		ks.load(new FileInputStream(keyFilename), password.toCharArray());
        String alias = (String)ks.aliases().nextElement();
        PrivateKey key = (PrivateKey)ks.getKey(alias, password.toCharArray());
        Certificate[] chain = ks.getCertificateChain(alias);

        PdfReader reader = new PdfReader(filename);
        FileOutputStream fout = new FileOutputStream(signedFilename);
        PdfStamper stp = PdfStamper.createSignature(reader, fout, '\0', null, append);

        PdfSignatureAppearance sap = stp.getSignatureAppearance();
        sap.setCrypto(key, chain, null, PdfSignatureAppearance.SELF_SIGNED);
        sap.setReason(reason);
        sap.setLocation(location);

        switch (signType) {
            case SIGNTYPE_VISIBLE
                sap.setVisibleSignature(rect, page, fieldName);
                break;
            case SIGNTYPE_FIELD
                sap.setVisibleSignature(fieldName);
                break;
            default
                break;
        }

        stp.close();
    }


private static Element newPara(String text, int alignment, int type) {
	Font font = FontFactory.getFont("Helvetica", 10, type, Color.BLACK);
	Paragraph p = new Paragraph(text, font);
	p.setAlignment(alignment);
	p.setLeading(font.size() * 1.2f);
	return p;
}


private static void copyFiles(File[] files, File destDir) throws IOException {
	for (int i = 0; i < files.length; i++) 
	{
		String dstName = destDir.getCanonicalPath() + File.separator + files[i].getName();
		InputStream is = new FileInputStream(files[i]);
		OutputStream os = new FileOutputStream(dstName);
		byte[] buffer = new byte[0x8000];
		int readBytes;
		while ((readBytes = is.read(buffer)) >= 0)
		{
			os.write(buffer, 0, readBytes);
		while ((readBytes = is.read(buffer)) >= 0)
		{
			os.write(buffer, 0, readBytes);
		}
		os.close();
		is.close();
	for (int i = 0; i < files.length; i++) 
	{
		String dstName = destDir.getCanonicalPath() + File.separator + files[i].getName();
		InputStream is = new FileInputStream(files[i]);
		OutputStream os = new FileOutputStream(dstName);
		byte[] buffer = new byte[0x8000];
		int readBytes;
		while ((readBytes = is.read(buffer)) >= 0)
		{
			os.write(buffer, 0, readBytes);
		}
		os.close();
		is.close();
	}
}

