/**
 * Prints 3 circles in different colors that intersect with eachother.
 * @param x
 * @param y
 * @param cb
 * @throws Exception
 */
public static void pictureCircles(float x, float y, PdfContentByte cb) throws Exception {
	cb.setColorFill(Color.red);
	cb.circle(x + 70, y + 70, 50);
	cb.fill();
	cb.setColorFill(Color.yellow);
	cb.circle(x + 100, y + 130, 50);
	cb.fill();
	cb.setColorFill(Color.blue);
	cb.circle(x + 130, y + 70, 50);
	cb.fill();
}

	cb.setColorStroke(Color.black);
	cb.setColorFill(Color.red);
	cb.rectangle(x, y, 100, 200);
	cb.fill();
	cb.setLineWidth(2);
	cb.rectangle(x, y, 200, 200);
	cb.stroke();
}


/**
 * Prints 3 circles in different colors that intersect with eachother.
 * @param x
 * @param y
 * @param cb
 * @throws Exception
 */
public static void pictureCircles(float x, float y, PdfContentByte cb) throws Exception {
	PdfGState gs = new PdfGState();
	gs.setBlendMode(PdfGState.BM_SOFTLIGHT);
	gs.setFillOpacity(0.7f);
	cb.setGState(gs);
	cb.setColorFill(Color.gray);
	cb.circle(x + 70, y + 70, 50);
	cb.fill();
	cb.circle(x + 100, y + 130, 50);
	cb.fill();
	cb.circle(x + 130, y + 70, 50);
	cb.fill();
}


/**
 * @see com.lowagie.text.pdf.PdfPageEventHelper#onCloseDocument(com.lowagie.text.pdf.PdfWriter, com.lowagie.text.Document)
 */
public void onCloseDocument(PdfWriter writer, Document document) {
	tpl.beginText();
	tpl.setFontAndSize(helv, 12);
	tpl.setTextMatrix(0, 0);
	tpl.showText("" + (writer.getPageNumber() - 1));
	tpl.endText();
}


/**
 * We only alter the handling of some endtags.
 */

    public void endElement(String uri, String lname, String name) {
        if (myTags.containsKey(name)) {
            XmlPeer peer = (XmlPeer) myTags.get(name);
            // we don't want the document to be close
            // because we are going to add a page after the xml is parsed
            if (isDocumentRoot(peer.getTag())) {
                return;
            }
            handleEndingTags(peer.getTag());
            // we want to add a paragraph after the speaker chunk
            if ("SPEAKER".equals(name)) {
                try {
                    TextElementArray previous = (TextElementArray) stack.pop();
                    previous.add(new Paragraph(16));
                    stack.push(previous);
                }
                catch(EmptyStackException ese) {
                }
            }
        }
        else {
            handleEndingTags(name);
        }
    }


/**
 * @see com.lowagie.text.SplitCharacter#isSplitCharacter(int, int, int, char[], com.lowagie.text.pdf.PdfChunk[])
 */
public boolean isSplitCharacter(int start, int current, int end, char[] cc, PdfChunk[] ck) {
	char c;
	if (ck == null)
		c = cc[current];
	else
		c = ck[Math.min(current, ck.length - 1)].getUnicodeEquivalent(cc[current]);
	return (c == '.');
}


public boolean isSplitCharacter(int start, int current, int end, char[] cc, PdfChunk[] ck) {
    char c;
    if (ck == null)
        c = cc[current];
    else
        c = ck[Math.min(current, ck.length - 1)].getUnicodeEquivalent(cc[current]);
    return (c == '.');
}

