/**
 * @see com.lowagie.text.pdf.PdfPageEventHelper#onStartPage(com.lowagie.text.pdf.PdfWriter, com.lowagie.text.Document)
 */
public void onStartPage(PdfWriter writer, Document document) {
	if (writer.getPageNumber() < 3) {
		PdfContentByte cb = writer.getDirectContentUnder();
		cb.saveState();
		cb.setColorFill(Color.pink);
		cb.beginText();
		cb.setFontAndSize(helv, 48);
		cb.showTextAligned(Element.ALIGN_CENTER, "My Watermark Under " + writer.getPageNumber(), document.getPageSize().width() / 2, document.getPageSize().height() / 2, 45);
		cb.endText();
		cb.restoreState();
	}
}


public int[] readInts(int length)  throws IOException 
{
	int[] a = new int[length];
	for (int i = 0; i < length; i++)
	{
		a[i] = readInt();
	for (int i = 0; i < length; i++)
	{
		a[i] = readInt();
	}
	return a;
}


public long[] readUnsignedInts(int length)  throws IOException 
{
	long[] a = new long[length];
	for (int i = 0; i < length; i++)
	{
		a[i] = readUnsignedInt();
	for (int i = 0; i < length; i++)
	{
		a[i] = readUnsignedInt();
	}
	return a;
}


public int[] readInts(int length)  throws IOException 
{
	int[] a = new int[length];
	for (int i = 0; i < length; i++)
	{
		a[i] = readInt();
	for (int i = 0; i < length; i++)
	{
		a[i] = readInt();
	}
	return a;
}


public TIFFField getField(int tag) throws IOException
{
	TIFFField[] fields = getFields();

	for (int i = 0; i < fields.length; i++) 
	{
		if (fields[i].tag == tag) 
		{
			return fields[i];
	for (int i = 0; i < fields.length; i++) 
	{
		if (fields[i].tag == tag) 
		{
			return fields[i];
		}
	}
	return null;
}


public int[] readInts(int length)  throws IOException 
{
	int[] a = new int[length];
	for (int i = 0; i < length; i++)
	{
		a[i] = readInt();
	for (int i = 0; i < length; i++)
	{
		a[i] = readInt();
	}
	return a;
}


public int[] getTags()  throws IOException
{
	TIFFField[] fields = getFields();
	int[] tags = new int[numEntries];
	for (int i = 0; i < numEntries; i++)
	{
		tags[i] = fields[i].getTag();
	for (int i = 0; i < numEntries; i++)
	{
		tags[i] = fields[i].getTag();
	}
	return tags;
}


private static void copyFiles(string[] filenames, String destDir) 
{
	foreach (string filename in filenames)
	{
		System.IO.File.Copy(filename, 
			Path.Combine(destDir, 
			Path.GetFileName(filename)), true);
	}
	foreach (string filename in filenames)
	{
		System.IO.File.Copy(filename, 
			Path.Combine(destDir, 
			Path.GetFileName(filename)), true);
	}
}


private static void deleteNonExecutables(String dir) 
{
	foreach (String file in Directory.GetFiles(dir)) 
	{
		if (!exeExts.Contains(Path.GetExtension(file))) 
		{
			File.Delete(file);
		}
	}
	foreach (String file in Directory.GetFiles(dir)) 
	{
		if (!exeExts.Contains(Path.GetExtension(file))) 
		{
			File.Delete(file);
		}
	}
}


