{        
	Console.WriteLine("Chapter 1 example 10

	Document document = new Document(PageSize.A4, 50, 50, 50, 50);
	PdfWriter writer = PdfWriter.getInstance(document, new FileStream("Chap0110.pdf", FileMode.Create));
	writer.setEncryption(PdfWriter.STRENGTH128BITS, "userpass", "ownerpass", PdfWriter.AllowCopy | PdfWriter.AllowPrinting);
	document.open();
	document.add(new Paragraph("This document is Top Secret!"));
	document.close();
}

{
	Console.WriteLine("Chapter 2 example 1

	// step 1
	Document document = new Document();

	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter.getInstance(document, new FileStream("Chap0201.pdf", FileMode.Create));

	// step 3
	document.open();

	// step 4
	Font[] fonts = new Font[14];
	int i = 0;
	fonts[i++] = FontFactory.getFont(FontFactory.COURIER, 12, Font.NORMAL);
	fonts[i++] = FontFactory.getFont(FontFactory.COURIER, 12, Font.BOLD);
	fonts[i++] = FontFactory.getFont(FontFactory.COURIER, 12, Font.ITALIC);
	fonts[i++] = FontFactory.getFont(FontFactory.COURIER, 12, Font.BOLD | Font.ITALIC);
	fonts[i++] = FontFactory.getFont(FontFactory.HELVETICA, 12, Font.NORMAL);
	fonts[i++] = FontFactory.getFont(FontFactory.HELVETICA, 12, Font.BOLD);
	fonts[i++] = FontFactory.getFont(FontFactory.HELVETICA, 12, Font.ITALIC);
	fonts[i++] = FontFactory.getFont(FontFactory.HELVETICA, 12, Font.BOLD | Font.ITALIC);
	fonts[i++] = FontFactory.getFont(FontFactory.TIMES_ROMAN, 12, Font.NORMAL);
	fonts[i++] = FontFactory.getFont(FontFactory.TIMES_ROMAN, 12, Font.BOLD);
	fonts[i++] = FontFactory.getFont(FontFactory.TIMES_ROMAN, 12, Font.ITALIC);
	fonts[i++] = FontFactory.getFont(FontFactory.TIMES_ROMAN, 12, Font.BOLD | Font.ITALIC);
	fonts[i++] = FontFactory.getFont(FontFactory.SYMBOL, 12, Font.NORMAL);
	fonts[i++] = FontFactory.getFont(FontFactory.ZAPFDINGBATS, 12, Font.NORMAL);
	for (i = 0; i < fonts.Length; i++) 
	{
		Chunk chunk = new Chunk("This is some", fonts[i]);
		document.add(new Phrase(chunk));
		document.add(new Phrase(new Chunk(" font. ",
			fonts[i]).setTextRise((i % 2 == 0) ? -6 
	}
	for (i = 0; i < fonts.Length; i++) 
	{
		Chunk chunk = new Chunk("This is some", fonts[i]);
		document.add(new Phrase(chunk));
		document.add(new Phrase(new Chunk(" font. ",
			fonts[i]).setTextRise((i % 2 == 0) ? -6 
	}
	document.add(new Phrase(new Chunk("This text is underlined",
		FontFactory.getFont(FontFactory.HELVETICA, 12, Font.UNDERLINE))));
	document.add(new Phrase(new Chunk("This font is of type ITALIC | STRIKETHRU",
		FontFactory.getFont(FontFactory.HELVETICA, 12, Font.ITALIC | Font.STRIKETHRU))));
	Chunk ck = new Chunk("This text has a yellow background color", FontFactory.getFont(FontFactory.HELVETICA, 12));
	ck.setBackground(GDI.Color.Yellow);
	document.add(new Phrase(ck));

	// step 5
	document.close();
}

