{
	Console.WriteLine("Chapter 9 example 2

	// step 1
	Document document = new Document();

	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter.getInstance(document, new FileStream("Chap0902.pdf", FileMode.Create));

	// step 3
	document.open();

	// step 4
	BaseFont bfComic = BaseFont.createFont((String)Microsoft.Win32.Registry.CurrentUser.
		OpenSubKey("Software").
		OpenSubKey("Microsoft").
		OpenSubKey("Windows").
		OpenSubKey("CurrentVersion").
		OpenSubKey("Explorer").
		OpenSubKey("Shell Folders").
		GetValue("Fonts") + java.io.File.separator + "comicbd.ttf", BaseFont.CP1252, BaseFont.NOT_EMBEDDED);
	Console.WriteLine("postscriptname
	String[] codePages = bfComic.getCodePagesSupported();
	for (int i = 0; i < codePages.Length; i++) 
	{
		Console.WriteLine(codePages[i]);                
	}
	for (int i = 0; i < codePages.Length; i++) 
	{
		Console.WriteLine(codePages[i]);                
	}
	String[][] names = bfComic.getFullFontName();
	for (int k = 0; k < names.Length; ++k) 
	{
		if (names[k][0].Equals("3") && names[k][1].Equals("1")) // Microsoft encoding
			Console.WriteLine(names[k][3]);
	}
	for (int k = 0; k < names.Length; ++k) 
	{
		if (names[k][0].Equals("3") && names[k][1].Equals("1")) // Microsoft encoding
			Console.WriteLine(names[k][3]);
	}


	Font font = new Font(bfComic, 12);
	String text1 = "This is the quite popular True Type font 'Comic'.";
	document.add(new Paragraph(text1, font));

	// step 5
	document.close();
}


	// we override the onGenericTag method
	public override void onGenericTag(PdfWriter writer, Document document, Rectangle rect, String text) 
	{
		speakers.add(new Speaker(text));
	}

	// we override the onOpenDocument method
	public override void onOpenDocument(PdfWriter writer, Document document) 
	{
		try 
		{
			bf = BaseFont.createFont(BaseFont.HELVETICA, BaseFont.CP1252, BaseFont.NOT_EMBEDDED);
			cb = writer.getDirectContent();
			template = cb.createTemplate(50, 50);
		}
		catch(DocumentException de) 
		{
		}
		catch(IOException ioe) 
		{
		}
	}

	// we override the onChapter method
	public override void onChapter(PdfWriter writer,Document document,float paragraphPosition,Paragraph title) 
	{
		System.Text.StringBuilder buf = new System.Text.StringBuilder();
		for (Iterator i = title.getChunks().iterator(); i.hasNext(); ) 
		{
			Chunk chunk = (Chunk) i.next();
			buf.Append(chunk.content());
		}
		act = buf.ToString();
	}

	// we override the onEndPage method
	public override void onEndPage(PdfWriter writer, Document document) 
	{
		int pageN = writer.getPageNumber();
		String text = "Page " + pageN + " of ";
		float len = bf.getWidthPoint(text, 8);
		cb.beginText();
		cb.setFontAndSize(bf, 8);
		cb.setTextMatrix(280, 30);
		cb.showText(text);
		cb.endText();
		cb.addTemplate(template, 280 + len, 30);
		cb.beginText();
		cb.setFontAndSize(bf, 8);
		cb.setTextMatrix(280, 820);
		if (pageN % 2 == 1) 
		{
			cb.showText("Romeo and Juliet");
		}
		else 
		{
			cb.showText(act);
		}
		cb.endText();
	}

	// we override the onCloseDocument method
	public override void onCloseDocument(PdfWriter writer, Document document) 
	{
		template.beginText();
		template.setFontAndSize(bf, 8);
		template.showText((writer.getPageNumber() - 1).ToString());
		template.endText();
	}

	// we add a method to retrieve the glossary
	public TreeSet getSpeakers() 
	{
		return speakers;
	}
}

