	public void tableLayout(PdfPTable table, float[][] width, float[] heights, int headerRows, int rowStart, PdfContentByte[] canvases) 
	{
		float[] widths = width[0];
		PdfContentByte cb = canvases[PdfPTable.TEXTCANVAS];
		cb.saveState();
		cb.setLineWidth(2);
		cb.setRGBColorStroke(255, 0, 0);
		cb.rectangle(widths[0], heights[heights.Length - 1], widths[widths.Length - 1] - widths[0], heights[0] - heights[heights.Length - 1]);
		cb.stroke();
		if (headerRows > 0) 
		{
			float headerHeight = heights[0];
			for (int k = 0; k < headerRows; ++k)
				headerHeight += heights[k];
			cb.setRGBColorStroke(0, 0, 255);
			cb.rectangle(widths[0], heights[headerRows], widths[widths.Length - 1] - widths[0], heights[0] - heights[headerRows]);
			cb.stroke();
		}
		cb.restoreState();
		cb = canvases[PdfPTable.BASECANVAS];
		cb.saveState();
		cb.setLineWidth(.5f);
		for (int line = 0; line < heights.Length - 1; ++line) 
		{

			widths = width[line];
			for (int col = 0; col < widths.Length - 1; ++col) 
			{
				if (line == 0 && col == 0)
					cb.setAction(new PdfAction("http
						widths[col], heights[line + 1], widths[col + 1], heights[line]);
				cb.setRGBColorStrokeF((float)rnd.NextDouble(), (float)rnd.NextDouble(), (float)rnd.NextDouble());
				cb.moveTo(widths[col], heights[line]);
				cb.lineTo(widths[col + 1], heights[line]);
				cb.stroke();
				cb.setRGBColorStrokeF((float)rnd.NextDouble(), (float)rnd.NextDouble(), (float)rnd.NextDouble());
				cb.moveTo(widths[col], heights[line]);
				cb.lineTo(widths[col], heights[line + 1]);
				cb.stroke();
			}
		}
		cb.restoreState();
	}
}

public class Chap1202 
{

	public static void Main(String[] args) 
	{

		Console.WriteLine("Chapter 12 example 2

		// step 1
		Document document = new Document(PageSize.A4, 50, 50, 50, 50);
		try 
		{
			// step 2
			PdfWriter writer = PdfWriter.getInstance(document, new FileStream("Chap1202.pdf", FileMode.Create));
			// step 3
			document.open();
			// step 4
			BaseFont bf = BaseFont.createFont(BaseFont.HELVETICA, BaseFont.WINANSI, BaseFont.NOT_EMBEDDED);
			// table 1
			PdfPTable table = new PdfPTable(4);
			table.getDefaultCell().setBorder(Rectangle.NO_BORDER);
			for (int k = 0; k < 24; ++k) 
			{
				if (k != 0)
					table.addCell("" + k);
				else
					table.addCell("This is an URL");
			}
			for (int k = 0; k < 24; ++k) 
			{
				if (k != 0)
					table.addCell("" + k);
				else
					table.addCell("This is an URL");
			}
			MyTableEvent aEvent = new MyTableEvent();
			table.setTableEvent(aEvent);
			table.setTotalWidth(300);
			// write table 1 at some position
			table.writeSelectedRows(0, -1, 100, 600, writer.getDirectContent());
			// add table 1 (default position)
			document.add(table);
			document.newPage();
			// table 2
			table = new PdfPTable(4);
			float fontSize = 12;
			table.getDefaultCell().setPaddingTop(bf.getFontDescriptor(BaseFont.ASCENT, fontSize) - fontSize + 2);
			table.getDefaultCell().setBorder(Rectangle.NO_BORDER);
			for (int k = 0; k < 500 * 4; ++k) 
			{
				if (k == 0)
					table.addCell(new Phrase("This is an URL", new Font(bf, fontSize)));
				else
					table.addCell(new Phrase("" + k, new Font(bf, fontSize)));
			}
			for (int k = 0; k < 500 * 4; ++k) 
			{
				if (k == 0)
					table.addCell(new Phrase("This is an URL", new Font(bf, fontSize)));
				else
					table.addCell(new Phrase("" + k, new Font(bf, fontSize)));
			}
			table.setTableEvent(aEvent);
			table.setHeaderRows(3);
			document.add(table);
		}
		catch (Exception de) 
		{
			Console.WriteLine(de.StackTrace + "\n" + de.ToString());
		}
		// step 5
		document.close();
	}


	// we override the onGenericTag method
	public override void onGenericTag(PdfWriter writer, Document document, Rectangle rect, String text) 
	{
		speakers.add(new Speaker(text));
	}

	// we override the onOpenDocument method
	public override void onOpenDocument(PdfWriter writer, Document document) 
	{
		try 
		{
			bf = BaseFont.createFont(BaseFont.HELVETICA, BaseFont.CP1252, BaseFont.NOT_EMBEDDED);
			cb = writer.getDirectContent();
			template = cb.createTemplate(50, 50);
		}
		catch(DocumentException de) 
		{
		}
		catch(IOException ioe) 
		{
		}
	}

	// we override the onChapter method
	public override void onChapter(PdfWriter writer,Document document,float paragraphPosition,Paragraph title) 
	{
		System.Text.StringBuilder buf = new System.Text.StringBuilder();
		for (Iterator i = title.getChunks().iterator(); i.hasNext(); ) 
		{
			Chunk chunk = (Chunk) i.next();
			buf.Append(chunk.content());
		}
		act = buf.ToString();
	}

	// we override the onEndPage method
	public override void onEndPage(PdfWriter writer, Document document) 
	{
		int pageN = writer.getPageNumber();
		String text = "Page " + pageN + " of ";
		float len = bf.getWidthPoint(text, 8);
		cb.beginText();
		cb.setFontAndSize(bf, 8);
		cb.setTextMatrix(280, 30);
		cb.showText(text);
		cb.endText();
		cb.addTemplate(template, 280 + len, 30);
		cb.beginText();
		cb.setFontAndSize(bf, 8);
		cb.setTextMatrix(280, 820);
		if (pageN % 2 == 1) 
		{
			cb.showText("Romeo and Juliet");
		}
		else 
		{
			cb.showText(act);
		}
		cb.endText();
	}

	// we override the onCloseDocument method
	public override void onCloseDocument(PdfWriter writer, Document document) 
	{
		template.beginText();
		template.setFontAndSize(bf, 8);
		template.showText((writer.getPageNumber() - 1).ToString());
		template.endText();
	}

	// we add a method to retrieve the glossary
	public TreeSet getSpeakers() 
	{
		return speakers;
	}
}

