private void AddRadio()
{
	PdfFormField radio = PdfFormField.createRadioButton(writer, true);
	PdfAppearance tpOff = cb.createAppearance(Height, Height);
	PdfAppearance tpOn = cb.createAppearance(Height, Height);

	tpOff.circle(Height/2, Height/2, (Height/2) * 0.9f);
	tpOff.stroke();

	tpOn.circle(Height/2, Height/2, (Height/2) * 0.9f);
	tpOn.stroke();
	tpOn.circle(Height/2, Height/2, (Height/2) * 0.3f);
	tpOn.fillStroke();

	radio.setFieldName("Radio");
	radio.setValueAsName("Button1");

	AddRadioKid("Button 1", "Button1", "Button1", radio, tpOn, tpOff);
	GoNextLine();
	AddRadioKid("Button 2", "Button2", "Off", radio, tpOn, tpOff);
	GoNextLine();
	AddRadioKid("Button 3", "Button3", "Off", radio, tpOn, tpOff);
	GoNextLine();

	writer.addAnnotation(radio);
}


private void AddCheckBox()
{
	PdfFormField field = PdfFormField.createCheckBox(writer);
	field.setFlags(flags);
	PdfAppearance tpOff = cb.createAppearance(Height, Height);
	PdfAppearance tpOn = cb.createAppearance(Height, Height);
	tpOff.rectangle(Height*0.1f, Height*0.1f, Height*0.8f, Height*0.8f);
	tpOff.stroke();

	tpOn.setRGBColorFill(255, 128, 128);
	tpOn.rectangle(Height*0.1f, Height*0.1f, Height*0.8f, Height*0.8f);
	tpOn.fillStroke();
	tpOn.moveTo(Height*0.1f, Height*0.1f);
	tpOn.lineTo(Height*0.9f, Height*0.9f);
	tpOn.moveTo(Height*0.1f, Height*0.9f);
	tpOn.lineTo(Height*0.9f, Height*0.1f);
	tpOn.stroke();

	field.setWidget(new Rectangle(PosX, PosY, PosX+Height, PosY+Height), PdfAnnotation.HIGHLIGHT_INVERT);
	field.setFieldName("CheckBox");
	field.setValueAsName("Off");
	field.setAppearanceState("Off");
	field.setAppearance(PdfAnnotation.APPEARANCE_NORMAL, "Off", tpOff);
	field.setAppearance(PdfAnnotation.APPEARANCE_NORMAL, "On", tpOn);
	writer.addAnnotation(field);

	cb.beginText();
	cb.setTextMatrix(PosX + Height*2, PosY + (Height-fontSize)/2 + (Height/2)*0.1f);
	cb.showText("Check Box");
	cb.endText();

	GoNextLine();
}


/**
 * @see com.lowagie.text.pdf.PdfPageEventHelper#onEndPage(com.lowagie.text.pdf.PdfWriter, com.lowagie.text.Document)
 */
public void onEndPage(PdfWriter writer, Document document) {
	try {
		Rectangle page = document.getPageSize();
		PdfPTable head = new PdfPTable(3);
		for (int k = 1; k <= 6; ++k)
			head.addCell("head " + k);
		for (int k = 1; k <= 6; ++k)
			head.addCell("head " + k);
		head.setTotalWidth(page.width() - document.leftMargin() - document.rightMargin());
		head.writeSelectedRows(0, -1, document.leftMargin(), page.height() - document.topMargin() + head.getTotalHeight(),
			writer.getDirectContent());
		PdfPTable foot = new PdfPTable(3);
		for (int k = 1; k <= 6; ++k)
			foot.addCell("foot " + k);
		for (int k = 1; k <= 6; ++k)
			foot.addCell("foot " + k);
		foot.setTotalWidth(page.width() - document.leftMargin() - document.rightMargin());
		foot.writeSelectedRows(0, -1, document.leftMargin(), document.bottomMargin(),
			writer.getDirectContent());
	}
	catch (Exception e) {
		throw new ExceptionConverter(e);
	}
}


/**
 * @see com.lowagie.text.pdf.PdfPageEventHelper#onEndPage(com.lowagie.text.pdf.PdfWriter, com.lowagie.text.Document)
 */
public void onEndPage(PdfWriter writer, Document document) {
	PdfContentByte cb = writer.getDirectContent();
	cb.saveState();
	// write the headertable
	table.setTotalWidth(document.right() - document.left());
	table.writeSelectedRows(0, -1, document.left(), document.getPageSize().height() - 50, cb);
	// compose the footer
	String text = "Page " + writer.getPageNumber() + " of ";
	float textSize = helv.getWidthPoint(text, 12);
	float textBase = document.bottom() - 20;
	cb.beginText();
	cb.setFontAndSize(helv, 12);
	// for odd pagenumbers, show the footer at the left
	if ((writer.getPageNumber() & 1) == 1) {
		cb.setTextMatrix(document.left(), textBase);
		cb.showText(text);
		cb.endText();
		cb.addTemplate(tpl, document.left() + textSize, textBase);
	}
		// for even numbers, show the footer at the right
	else {
		float adjust = helv.getWidthPoint("0", 12);
		cb.setTextMatrix(document.right() - textSize - adjust, textBase);
		cb.showText(text);
		cb.endText();
		cb.addTemplate(tpl, document.right() - adjust, textBase);
	}
	cb.saveState();
	// draw a Rectangle around the page
	cb.setColorStroke(Color.orange);
	cb.setLineWidth(2);
	cb.rectangle(20, 20, document.getPageSize().width() - 40, document.getPageSize().height() - 40);
	cb.stroke();
	cb.restoreState();
	// starting on page 3, a watermark with an Image that is made transparent
	if (writer.getPageNumber() >= 3) {
		cb.setGState(gstate);
		cb.setColorFill(Color.red);
		cb.beginText();
		cb.setFontAndSize(helv, 48);
		cb.showTextAligned(Element.ALIGN_CENTER, "Watermark Opacity " + writer.getPageNumber(), document.getPageSize().width() / 2, document.getPageSize().height() / 2, 45);
		cb.endText();
		try {
			cb.addImage(headerImage, headerImage.width(), 0, 0, headerImage.height(), 440, 80);
		}
		catch(Exception e) {
			throw new ExceptionConverter(e);
		}
		cb.restoreState();
	}
}

public TIFFField[] getFields()  throws IOException 
{
	if (fields != null) return fields;

	fields = new TIFFField[numEntries];

	for (int i = 0; i < numEntries; i++) 
	{
		reader.seek(IFDOffset + 2 + TIFFField.size * i);
		int tag = reader.readShort();
		int type = reader.readShort();
		int count = reader.readInt();

		int sizeOfValue = TIFFField.sizeOfType(type) * count;
		long offset = sizeOfValue > 4 ? reader.readInt() 
		reader.seek(offset);

		TIFFValueProxy data = new TIFFValueProxy(reader, reader.getFilePointer(), sizeOfValue, isBigEndian);

		fields[i] = new TIFFField(tag, type, count, data);
	for (int i = 0; i < numEntries; i++) 
	{
		reader.seek(IFDOffset + 2 + TIFFField.size * i);
		int tag = reader.readShort();
		int type = reader.readShort();
		int count = reader.readInt();

		int sizeOfValue = TIFFField.sizeOfType(type) * count;
		long offset = sizeOfValue > 4 ? reader.readInt() 
		reader.seek(offset);

		TIFFValueProxy data = new TIFFValueProxy(reader, reader.getFilePointer(), sizeOfValue, isBigEndian);

		fields[i] = new TIFFField(tag, type, count, data);
	}
	return fields;
}


private java.lang.Number getAsObject(int index) throws IOException
{
	Object o = null;
	data.stream.seek(data.position + sizeOfType(type) * index);
	switch (type)
	{
	case TIFF_SBYTE
	case TIFF_UNDEFINED
		return new java.lang.Byte(data.stream.readByte());
	case TIFF_BYTE
		new java.lang.Short(data.stream.readUnsignedByte());
	case TIFF_SSHORT
		return  new java.lang.Short(data.stream.readShort());
	case TIFF_SHORT
		return  new java.lang.Integer(data.stream.readUnsignedShort());
	case TIFF_SLONG
		return new java.lang.Integer(data.stream.readInt());
	case TIFF_LONG
		return new java.lang.Long(data.stream.readUnsignedInt());
	case TIFF_FLOAT
		return new java.lang.Float(data.stream.readFloat());
	case TIFF_DOUBLE
		return new java.lang.Double(data.stream.readDouble());
	case TIFF_RATIONAL
		return new TIFFRational(data.stream.readUnsignedInts(2));
	case TIFF_SRATIONAL
		return new TIFFSRational(data.stream.readInts(2));
	default
		throw new IOException("Not supported on getAsObject().");
	}

}


/**
 * @see com.lowagie.text.pdf.PdfPageEventHelper#onOpenDocument(com.lowagie.text.pdf.PdfWriter, com.lowagie.text.Document)
 */
public void onOpenDocument(PdfWriter writer, Document document) {
	try {
		// initialization of the header table
		headerImage = Image.getInstance("logo.gif");
		table = new PdfPTable(2);
		Phrase p = new Phrase();
		Chunk ck = new Chunk("lowagie.com\n", new Font(Font.TIMES_ROMAN, 16, Font.BOLDITALIC, Color.blue));
		p.add(ck);
		ck = new Chunk("Ghent\nBelgium", new Font(Font.HELVETICA, 12, Font.NORMAL, Color.darkGray));
		p.add(ck);
		table.getDefaultCell().setBackgroundColor(Color.yellow);
		table.getDefaultCell().setBorderWidth(0);
		table.addCell(p);
		table.getDefaultCell().setHorizontalAlignment(Element.ALIGN_RIGHT);
		table.addCell(new Phrase(new Chunk(headerImage, 0, 0)));
		// initialization of the Graphic State
		gstate = new PdfGState();
		gstate.setFillOpacity(0.3f);
		gstate.setStrokeOpacity(0.3f);
		// initialization of the template
		tpl = writer.getDirectContent().createTemplate(100, 100);
		tpl.setBoundingBox(new Rectangle(-20, -20, 100, 100));
		// initialization of the font
		helv = BaseFont.createFont("Helvetica", BaseFont.WINANSI, false);
	}
	catch(Exception e) {
		throw new ExceptionConverter(e);
	}
}


/**
 * Method that generates the actual PDF file.
 */
public void generatePdf() throws Exception {   

	// step 1
	Rectangle pageSize = new Rectangle(780, 525);
	if (backgroundcolor != null) pageSize.setBackgroundColor(backgroundcolor);
	Document document = new Document(pageSize);

	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	if (filename == null) { filename = "dvdcover.pdf"; }
	PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream(filename));

	// step 3
	document.open();

	// step 4
	PdfContentByte cb = writer.getDirectContent();
	if (title != null) {
		cb.setFontAndSize(BaseFont.createFont(BaseFont.HELVETICA, BaseFont.WINANSI, false), 24);
		cb.beginText();
		if (front == null) {
			cb.showTextAligned(Element.ALIGN_CENTER, title, 595f, 262f, 0f);
		}
		if (side == null) {
			cb.showTextAligned(Element.ALIGN_CENTER, title, 385f, 262f, 270f);
		}
		cb.endText(); 
	}
	cb.moveTo(370, 0);
	cb.lineTo(370, 525);
	cb.moveTo(410, 525);
	cb.lineTo(410, 0);
	cb.stroke();
	if (front != null) {
		front.scaleToFit(370, 525);
		front.setAbsolutePosition(410f + (370f - front.scaledWidth()) / 2f, (525f - front.scaledHeight()) / 2f);
		document.add(front);
	}
	if (back != null) {
		back.scaleToFit(370, 525);
		back.setAbsolutePosition((370f - back.scaledWidth()) / 2f, (525f - back.scaledHeight()) / 2f);
		document.add(back);
	}
	if (side != null) {
		side.scaleToFit(40, 525);
		side.setAbsolutePosition(370 + (40f - side.scaledWidth()) / 2f, (525f - side.scaledHeight()) / 2f);
		document.add(side);
	}

	// step 5
	document.close();
}


private void AddText()
{
	String text = "Fill here.";

	PdfFormField field = PdfFormField.createTextField(writer, false, false, 0);
	field.setFlags(flags);
	field.setWidget(new Rectangle(PosX, PosY, PosX + Width, PosY + Height), PdfAnnotation.HIGHLIGHT_INVERT);
	field.setFieldName("ATextField");
	field.setValueAsString(text);
	field.setDefaultValueAsString(text);
	//field.setMKBorderColor(BorderColor);
	//field.setMKBackgroundColor(BackColor);
	field.setBorderStyle(new PdfBorderDictionary(BorderWidth, PdfBorderDictionary.STYLE_SOLID));
	field.setPage();

	PdfAppearance tp = cb.createAppearance(Width, Height);
	PdfAppearance da = (PdfAppearance)tp.getDuplicate();

	//setup a default appearance string, DA entry.
	da.setFontAndSize(helv, fontSize);
	da.setColorFill(textColor);
	field.setDefaultAppearanceString(da);

	//setup an appearance dictionary, PA entry (optional).
	tp.beginVariableText();
	tp.saveState(); // q
	tp.rectangle(BorderWidth, BorderWidth, Width - BorderWidth*2, Height - BorderWidth*2);
	tp.clip(); //W
	tp.newPath(); //n
	tp.beginText();
	tp.setFontAndSize(helv, fontSize);
	tp.setColorFill(uneditedColor);
	tp.setTextMatrix(BorderWidth*2, (Height - fontSize)/2 + fontSize/8);
	tp.showText(text);
	tp.endText();
	tp.restoreState(); //Q
	tp.endVariableText();
	field.setAppearance(PdfAnnotation.APPEARANCE_NORMAL, tp);

	writer.addAnnotation(field);
	GoNextLine();
}


private void AddCombo()
{
	String[] options = {"Red", "Green", "Blue"};
	PdfFormField field;

	field = PdfFormField.createCombo(writer, true, options, 0);
	field.setFlags(flags);
	field.setWidget(new Rectangle(PosX, PosY, PosX+Width, PosY+Height), PdfAnnotation.HIGHLIGHT_INVERT);
	field.setFieldName("ACombo1");
	field.setValueAsString("");
	writer.addAnnotation(field);
	GoNextLine();

	field = PdfFormField.createCombo(writer, true, options, 0);
	field.setWidget(new Rectangle(PosX, PosY, PosX+Width, PosY+ Height), PdfAnnotation.HIGHLIGHT_INVERT);
	field.setFieldName("ACombo2");
	field.setValueAsString("Default");

	PdfAppearance pa = cb.createAppearance(Width, Height);
	//PdfAppearance da = (PdfAppearance)pa.getDuplicate();
	//
	//da.setFontAndSize(helv, fontSize);
	//field.setDefaultAppearanceString(da);

	pa.beginVariableText();
	pa.saveState();
	pa.setColorStroke(GDI.Color.Black);
	pa.closePathStroke();
	pa.beginText();
	pa.setFontAndSize(helv, fontSize);
	pa.showText("Select here.");
	pa.endText();
	pa.restoreState();
	pa.endVariableText();
	field.setAppearance(PdfAnnotation.APPEARANCE_NORMAL, pa);

	writer.addAnnotation(field);
	GoNextLine();
}


