/**
 * Generic page event.
 * 
 * @param args no arguments needed here
 */
public static void main(String[] args) throws Exception {

	System.out.println("Generic");

	// step 1
	Document document = new Document();
	// step 2
	// we create a writer that listens to the document
	PdfWriter writer = PdfWriter.getInstance(document,
		new FileOutputStream("Generic.pdf"));
	writer.setPageEvent(new Generic());

	// step 3
	document.open();
	// step 4
	Paragraph p = new Paragraph("Generic page event");
	document.add(p);
	Chunk box = new Chunk("box");
	box.setGenericTag("box");
	Chunk ellipse = new Chunk("ellipse");
	ellipse.setGenericTag("ellipse");
	p = new Paragraph("In this example, we will add chunks that are tagged as an ");
	p.add(ellipse);
	p.add(" and chunks that are tagged as a ");
	p.add(box);
	p.add(". Can you see the difference between ");
	Chunk c1 = new Chunk("this");
	c1.setGenericTag("box");
	Chunk c2 = new Chunk("that");
	c2.setGenericTag("ellipse");
	p.add(c1);
	p.add(" and ");
	p.add(c2);
	p.add("? One is a ");
	p.add(box);
	p.add("; the other an ");
	p.add(ellipse);
	document.add(p);
	// step 5
	document.close();
}

	PdfReader reader1 = new PdfReader("SimpleRegistrationForm.pdf");
	PdfReader reader2 = new PdfReader("TextFields.pdf");
	PdfCopyFields copy = new PdfCopyFields(new FileOutputStream("concatenatedforms.pdf"));
	copy.addDocument(reader1);
	copy.addDocument(reader2);
	copy.close();
}

	System.out.println("Fonts in the FontFactory");

	// step 1
	Document document = new Document();

	// step 2
	// we create a writer that listens to the document
	PdfWriter.getInstance(document,
		new FileOutputStream("FontFactory.pdf"));

	// step 3
	document.open();
	// step 4
	String name;
	Paragraph p = new Paragraph("Font Families", FontFactory.getFont(FontFactory.HELVETICA, 16f));
	document.add(p);
	FontFactory.registerDirectories();
	TreeSet families = new TreeSet(FontFactory.getRegisteredFamilies());
	for (Iterator i = families.iterator(); i.hasNext(); ) {
		name = (String) i.next();
		p = new Paragraph(name);
		document.add(p);
	for (Iterator i = families.iterator(); i.hasNext(); ) {
		name = (String) i.next();
		p = new Paragraph(name);
		document.add(p);
	}
	document.newPage();
	String quick = "quick brown fox jumps over the lazy dog";
	p = new Paragraph("Fonts", FontFactory.getFont(FontFactory.HELVETICA, 16f));
	TreeSet fonts = new TreeSet(FontFactory.getRegisteredFonts());
	for (Iterator i = families.iterator(); i.hasNext(); ) {
		name = (String) i.next();
		p = new Paragraph(name);
		document.add(p);
		try {
			p = new Paragraph(quick, FontFactory.getFont(name, BaseFont.WINANSI, BaseFont.EMBEDDED));
			document.add(p);
		}
		catch (Exception e) {
			document.add(new Paragraph(e.getMessage()));
	for (Iterator i = families.iterator(); i.hasNext(); ) {
		name = (String) i.next();
		p = new Paragraph(name);
		document.add(p);
		try {
			p = new Paragraph(quick, FontFactory.getFont(name, BaseFont.WINANSI, BaseFont.EMBEDDED));
			document.add(p);
		}
		catch (Exception e) {
			document.add(new Paragraph(e.getMessage()));
		}
	}

	// step 5
	document.close();
}

	PdfReader reader1 = new PdfReader("SimpleRegistrationForm.pdf");
	PdfReader reader2 = new PdfReader("TextFields.pdf");
	PdfCopyFields copy = new PdfCopyFields(new FileOutputStream("concatenatedforms.pdf"));
	copy.addDocument(reader1);
	copy.addDocument(reader2);
	copy.close();
}

	System.out.println("Actions");

	// step 1
	Document document = new Document();
	Document remote = new Document();

	// step 2
	PdfWriter writer = PdfWriter.getInstance(document,
		new FileOutputStream("Actions.pdf"));
	PdfWriter.getInstance(remote, new FileOutputStream("remote.pdf"));
	// step 3
	document.open();
	remote.open();
	// step 4
	PdfAction action = PdfAction.gotoLocalPage(2, new PdfDestination(
		PdfDestination.XYZ, -1, 10000, 0), writer);
	writer.setOpenAction(action);
	document.add(new Paragraph("Page 1"));
	document.newPage();
	document.add(new Paragraph("Page 2"));
	document.add(new Chunk("goto page 1").setAction(PdfAction
		.gotoLocalPage(1, new PdfDestination(PdfDestination.FITH,
		500), writer)));
	document.add(Chunk.NEWLINE);
	document.add(new Chunk("goto another document").setAction(PdfAction
		.gotoRemotePage("remote.pdf", "test", false, true)));
	remote.add(new Paragraph("Some remote document"));
	remote.newPage();
	Paragraph p = new Paragraph("This paragraph contains a ");
	p.add(new Chunk("local destination").setLocalDestination("test"));
	remote.add(p);

	// step 5
	document.close();
	remote.close();
}

	PdfReader reader1 = new PdfReader("SimpleRegistrationForm.pdf");
	PdfReader reader2 = new PdfReader("TextFields.pdf");
	PdfCopyFields copy = new PdfCopyFields(new FileOutputStream("concatenatedforms.pdf"));
	copy.addDocument(reader1);
	copy.addDocument(reader2);
	copy.close();
}

