/**
 * Prints 3 circles in different colors that intersect with eachother.
 * @param x
 * @param y
 * @param cb
 * @throws Exception
 */
public static void pictureCircles(float x, float y, PdfContentByte cb) throws Exception {
	cb.setColorFill(Color.red);
	cb.circle(x + 70, y + 70, 50);
	cb.fill();
	cb.setColorFill(Color.yellow);
	cb.circle(x + 100, y + 130, 50);
	cb.fill();
	cb.setColorFill(Color.blue);
	cb.circle(x + 130, y + 70, 50);
	cb.fill();
}

	cb.setColorStroke(Color.black);
	cb.setColorFill(Color.red);
	cb.rectangle(x, y, 100, 200);
	cb.fill();
	cb.setLineWidth(2);
	cb.rectangle(x, y, 200, 200);
	cb.stroke();
}


/**
 * Prints 3 circles in different colors that intersect with eachother.
 * @param x
 * @param y
 * @param cb
 * @throws Exception
 */
public static void pictureCircles(float x, float y, PdfContentByte cb) throws Exception {
	PdfGState gs = new PdfGState();
	gs.setBlendMode(PdfGState.BM_SOFTLIGHT);
	gs.setFillOpacity(0.7f);
	cb.setGState(gs);
	cb.setColorFill(Color.gray);
	cb.circle(x + 70, y + 70, 50);
	cb.fill();
	cb.circle(x + 100, y + 130, 50);
	cb.fill();
	cb.circle(x + 130, y + 70, 50);
	cb.fill();
}


/**
 * @see com.lowagie.text.SplitCharacter#isSplitCharacter(int, int, int, char[], com.lowagie.text.pdf.PdfChunk[])
 */
public boolean isSplitCharacter(int start, int current, int end, char[] cc, PdfChunk[] ck) {
	char c;
	if (ck == null)
		c = cc[current];
	else
		c = ck[Math.min(current, ck.length - 1)].getUnicodeEquivalent(cc[current]);
	return (c == '.');
}


public boolean isSplitCharacter(int start, int current, int end, char[] cc, PdfChunk[] ck) {
    char c;
    if (ck == null)
        c = cc[current];
    else
        c = ck[Math.min(current, ck.length - 1)].getUnicodeEquivalent(cc[current]);
    return (c == '.');
}

