        p1.add(" just here.");
        p1.setLeading(img.scaledHeight() * 1.1f);
        document.add(p1);
        document.add(table);
        Paragraph p2 = new Paragraph("More templates ");
        p2.setLeading(img.scaledHeight() * 1.1f);
        p2.setAlignment(Element.ALIGN_JUSTIFIED);
        img.scalePercent(70);
        for (int k = 0; k < 20; ++k)
            p2.add(ck);
        for (int k = 0; k < 20; ++k)
            p2.add(ck);
        document.add(p2);
        // step 5
        document.close();
    }
