		mct.addElement(newPara(randomWord(adverb), Element.ALIGN_LEFT, Font.NORMAL));
		mct.addElement(newPara("\n\n", Element.ALIGN_LEFT, Font.NORMAL));
	for (int i = 0; i < 30; i++) {
