            cell.add(new Paragraph("A"));
            row.add(cell);
            cell = new SimpleCell(SimpleCell.CELL);
            cell.add(new Paragraph("B"));
            cell.setBackgroundColor(Color.gray);
            row.add(cell);
            cell = new SimpleCell(SimpleCell.CELL);
            cell.add(new Paragraph("C"));
            row.add(cell);
        }
        table.addElement(row);
    for (int i = 0; i < 100; i++) {
        row = new SimpleCell(SimpleCell.ROW);
