	System.out.println("Encodings");

	// step 1
	Document document = new Document(PageSize.A4, 50, 50, 50, 50);
	// step 2
	PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("encodingfont.pdf"));
	// step 3
	document.open();
	// step 4
	String all[] = {"Symbol", "ZapfDingbats"};
	Font hex = new Font(Font.HELVETICA, 5);
	for (int z = 0; z < all.length; ++z) {
		String file = all[z];
		document.add(new Paragraph("Unicode equivalence for the font \""
			+ file
			+"\" with the encoding \""
			+file
			+ "\"\n"));
		char tb[];
		if (z == 0)
			tb = table_symbol;
		else
			tb = table_dingbats;
		BaseFont bf;
		bf = BaseFont.createFont(file, file, true);
		Font f = new Font(bf, 12);
		PdfPTable table = new PdfPTable(16);
		table.setWidthPercentage(100);
		table.getDefaultCell().setBorderWidth(1);
		table.getDefaultCell().setHorizontalAlignment(Element.ALIGN_CENTER);
		for (int k = 0; k < tb.length; ++k) {
			char c = tb[k];
			if (bf.charExists(c)) {
				Phrase ph = new Phrase(12, new String(new char[]{c}), f);
				ph.add(new Phrase(12, "\n\n" + cst(c), hex));
				table.addCell(ph);
			}
			else {
				Phrase ph = new Phrase("\u00a0");
				ph.add(new Phrase(12, "\n\n" + cst(c), hex));
				table.addCell(ph);
		for (int k = 0; k < tb.length; ++k) {
			char c = tb[k];
			if (bf.charExists(c)) {
				Phrase ph = new Phrase(12, new String(new char[]{c}), f);
				ph.add(new Phrase(12, "\n\n" + cst(c), hex));
				table.addCell(ph);
			}
			else {
				Phrase ph = new Phrase("\u00a0");
				ph.add(new Phrase(12, "\n\n" + cst(c), hex));
				table.addCell(ph);
			}
		}
		document.add(table);
		document.newPage();
	for (int z = 0; z < all.length; ++z) {
		String file = all[z];
		document.add(new Paragraph("Unicode equivalence for the font \""
			+ file
			+"\" with the encoding \""
			+file
			+ "\"\n"));
		char tb[];
		if (z == 0)
			tb = table_symbol;
		else
			tb = table_dingbats;
		BaseFont bf;
		bf = BaseFont.createFont(file, file, true);
		Font f = new Font(bf, 12);
		PdfPTable table = new PdfPTable(16);
		table.setWidthPercentage(100);
		table.getDefaultCell().setBorderWidth(1);
		table.getDefaultCell().setHorizontalAlignment(Element.ALIGN_CENTER);
		for (int k = 0; k < tb.length; ++k) {
			char c = tb[k];
			if (bf.charExists(c)) {
				Phrase ph = new Phrase(12, new String(new char[]{c}), f);
				ph.add(new Phrase(12, "\n\n" + cst(c), hex));
				table.addCell(ph);
			}
			else {
				Phrase ph = new Phrase("\u00a0");
				ph.add(new Phrase(12, "\n\n" + cst(c), hex));
				table.addCell(ph);
			}
		}
		document.add(table);
		document.newPage();
	}
	// step 5
	document.close();
}

