    Document.compress = false;
    System.out.println("Chapter 6 example 14
    // step 1
    Document document = new Document();
    try {
        // step 2
        // we create a writer that listens to the document
        // and directs a PDF-stream to a file
        PdfWriter.getInstance(document, new FileOutputStream("Chap0614.pdf"));
        // step 3
        document.open();
        // step 4
        Image img = Image.getInstance("pngnow.png");
        img.scalePercent(70);
        Chunk ck = new Chunk(img, 0, -5);
        Table table = new Table(3);
        table.setWidth(100);
        table.setPadding(2);
        table.setDefaultHorizontalAlignment(Element.ALIGN_CENTER);
        Cell cell = new Cell(new Chunk(img, 0, -13));
        cell.setBackgroundColor(new Color(0xC0, 0xC0, 0xC0));
        cell.setHorizontalAlignment(Element.ALIGN_CENTER);
        table.addCell("I see an image\non my right");
        table.addCell(cell);
        table.addCell("I see an image\non my left");
        table.addCell(cell);
        table.addCell("I see images\neverywhere");
        table.addCell(cell);
        table.addCell("I see an image\non my right");
        table.addCell(cell);
        table.addCell("I see an image\non my left");

        Phrase p1 = new Phrase("This is an image ");
        p1.add(ck);
        p1.add(" just here.");
        document.add(p1);
        document.add(p1);
        document.add(p1);
        document.add(p1);
        document.add(p1);
        document.add(p1);
        document.add(p1);
        document.add(table);
    }
    catch(DocumentException de) {
        System.out.println(de.getMessage());
    }
    catch(IOException ioe) {
        System.out.println(ioe.getMessage());
    }
    // step 5
    document.close();
}

	Document.compress = false;
	System.out.println("Transformating an Image");        
	// step 1
	Document document = new Document(PageSize.A4);

	// step 2
	PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("transformimage.pdf"));

	// step 3
	document.open();

	// step 4
	PdfContentByte cb = writer.getDirectContent();
	Image img = Image.getInstance("hitchcock.png");
	cb.addImage(img, 271, -50, -30, 550, 100, 100);

	// step 5
	document.close();
}

	BarcodePDF417 pdf417 = new BarcodePDF417();
	String text = "It was the best of times, it was the worst of times, " + 
		"it was the age of wisdom, it was the age of foolishness, " +
		"it was the epoch of belief, it was the epoch of incredulity, " +
		"it was the season of Light, it was the season of Darkness, " +
		"it was the spring of hope, it was the winter of despair, " +
		"we had everything before us, we had nothing before us, " +
		"we were all going direct to Heaven, we were all going direct " +
		"the other way - in short, the period was so far like the present " +
		"period, that some of its noisiest authorities insisted on its " +
		"being received, for good or for evil, in the superlative degree " +
		"of comparison only.";
	pdf417.setText(text);
	Document document = new Document(PageSize.A4, 50, 50, 50, 50);
	PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("pdf417.pdf"));
	document.open();
	Image img = pdf417.getImage();
	img.scalePercent(50, 50 * pdf417.getYHeight());
	document.add(img);
	document.close();
}

