}

public long[] getAsLongs() throws IOException
{ 
	data.stream.seek(data.position);
	long[] a = new long[data.size];
	for (int i = 0; i < a.length; i++) {
		a[i] = data.stream.readUnsignedInt();
	for (int i = 0; i < a.length; i++) {
		a[i] = data.stream.readUnsignedInt();
	}
	return a;
}

