	String fontsDirName = (String)Microsoft.Win32.Registry.CurrentUser.
		OpenSubKey("Software").
		OpenSubKey("Microsoft").
		OpenSubKey("Windows").
		OpenSubKey("CurrentVersion").
		OpenSubKey("Explorer").
		OpenSubKey("Shell Folders").
