	Document document = new Document(PageSize.A4, 50, 50, 50, 50);
	// step 2
	PdfWriter writer = PdfWriter.getInstance(document, new FileStream("Chap1009.pdf", FileMode.Create));
	// step 3
	document.open();
	// step 4
