	System.out.println("Changing the style of a FontFactory font");

	// step 1
	Document document = new Document();

	// step 2
	PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("fontfactorystyles.pdf"));

	// step 3
	document.open();

	// step 4
	FontFactory.register(Configuration.fontsDirectory + File.separator + "arial.ttf");
	FontFactory.register(Configuration.fontsDirectory + File.separator + "arialbd.ttf");
	FontFactory.register(Configuration.fontsDirectory + File.separator + "ariali.ttf");
	FontFactory.register(Configuration.fontsDirectory + File.separator + "arialbi.ttf");
	Phrase myPhrase = new Phrase("This is font family Arial ", FontFactory.getFont("Arial", 8));
	myPhrase.add(new Phrase("italic ", FontFactory.getFont("Arial", 8, Font.ITALIC)));
	myPhrase.add(new Phrase("bold ", FontFactory.getFont("Arial", 8, Font.BOLD)));
	myPhrase.add(new Phrase("bolditalic", FontFactory.getFont("Arial", 8, Font.BOLDITALIC)));
	document.add(myPhrase);

	// step 5
	document.close();
}

