public static void loadDocument(Document document) {
    String[] bogusData = { "M0065920",
    "SL",
    "FR86000P",
    "PCGOLD",
    "119000",
    "96 06",
    "2001-08-13",
    "4350",
    "6011648299",
    "FLFLMTGP",
    "153",
    "119000.00"
    };
    int NumColumns = 12;
    try {
        // we add some meta information to the document

        PdfPTable datatable = new PdfPTable(NumColumns);

        datatable.getDefaultCell().setPadding(3);
        int headerwidths[] = {9, 4, 8, 10, 8, 11, 9, 7, 9, 10, 4, 10}; // percentage
        datatable.setWidths(headerwidths);
        datatable.setWidthPercentage(100); // percentage

        datatable.getDefaultCell().setBorderWidth(2);
        datatable.getDefaultCell().setHorizontalAlignment(Element.ALIGN_CENTER);
        datatable.addCell("Clock #");
        datatable.addCell("Trans Type");
        datatable.addCell("Cusip");
        datatable.addCell("Long Name");
        datatable.addCell("Quantity");
        datatable.addCell("Fraction Price");
        datatable.addCell("Settle Date");
        datatable.addCell("Portfolio");
        datatable.addCell("ADP Number");
        datatable.addCell("Account ID");
        datatable.addCell("Reg Rep ID");
        datatable.addCell("Amt To Go ");

        datatable.setHeaderRows(1);  // this is the end of the table header

        datatable.getDefaultCell().setBorderWidth(1);

        int max = 666;
        for (int i = 1; i < max; i++) {
            if (i % 2 == 1) {
                datatable.getDefaultCell().setGrayFill(0.9f);
            }
            for (int x = 0; x < NumColumns; x++) {
                datatable.addCell(bogusData[x]);
            for (int x = 0; x < NumColumns; x++) {
                datatable.addCell(bogusData[x]);
            }
            if (i % 2 == 1) {
                datatable.getDefaultCell().setGrayFill(0.0f);
        for (int i = 1; i < max; i++) {
            if (i % 2 == 1) {
                datatable.getDefaultCell().setGrayFill(0.9f);
            }
            for (int x = 0; x < NumColumns; x++) {
                datatable.addCell(bogusData[x]);
            }
            if (i % 2 == 1) {
                datatable.getDefaultCell().setGrayFill(0.0f);
            }
        }
        document.add(datatable);
    }
    catch(Exception e) {
        e.printStackTrace();
    }
}

{
	float[] widths = width[0];
	PdfContentByte cb = canvases[PdfPTable.TEXTCANVAS];
	cb.saveState();
	cb.setLineWidth(2);
	cb.setRGBColorStroke(255, 0, 0);
	cb.rectangle(widths[0], heights[heights.Length - 1], widths[widths.Length - 1] - widths[0], heights[0] - heights[heights.Length - 1]);
	cb.stroke();
	if (headerRows > 0) 
	{
		float headerHeight = heights[0];
		for (int k = 0; k < headerRows; ++k)
			headerHeight += heights[k];
		for (int k = 0; k < headerRows; ++k)
			headerHeight += heights[k];
		cb.setRGBColorStroke(0, 0, 255);
		cb.rectangle(widths[0], heights[headerRows], widths[widths.Length - 1] - widths[0], heights[0] - heights[headerRows]);
		cb.stroke();
	}
	cb.restoreState();
	cb = canvases[PdfPTable.BASECANVAS];
	cb.saveState();
	cb.setLineWidth(.5f);
	for (int line = 0; line < heights.Length - 1; ++line) 
	{

		widths = width[line];
		for (int col = 0; col < widths.Length - 1; ++col) 
		{
			if (line == 0 && col == 0)
				cb.setAction(new PdfAction("http
					widths[col], heights[line + 1], widths[col + 1], heights[line]);
			cb.setRGBColorStrokeF((float)rnd.NextDouble(), (float)rnd.NextDouble(), (float)rnd.NextDouble());
			cb.moveTo(widths[col], heights[line]);
			cb.lineTo(widths[col + 1], heights[line]);
			cb.stroke();
			cb.setRGBColorStrokeF((float)rnd.NextDouble(), (float)rnd.NextDouble(), (float)rnd.NextDouble());
			cb.moveTo(widths[col], heights[line]);
			cb.lineTo(widths[col], heights[line + 1]);
			cb.stroke();
		}
		for (int col = 0; col < widths.Length - 1; ++col) 
		{
			if (line == 0 && col == 0)
				cb.setAction(new PdfAction("http
					widths[col], heights[line + 1], widths[col + 1], heights[line]);
			cb.setRGBColorStrokeF((float)rnd.NextDouble(), (float)rnd.NextDouble(), (float)rnd.NextDouble());
			cb.moveTo(widths[col], heights[line]);
			cb.lineTo(widths[col + 1], heights[line]);
			cb.stroke();
			cb.setRGBColorStrokeF((float)rnd.NextDouble(), (float)rnd.NextDouble(), (float)rnd.NextDouble());
			cb.moveTo(widths[col], heights[line]);
			cb.lineTo(widths[col], heights[line + 1]);
			cb.stroke();
		}
	}
	for (int line = 0; line < heights.Length - 1; ++line) 
	{

		widths = width[line];
		for (int col = 0; col < widths.Length - 1; ++col) 
		{
			if (line == 0 && col == 0)
				cb.setAction(new PdfAction("http
					widths[col], heights[line + 1], widths[col + 1], heights[line]);
			cb.setRGBColorStrokeF((float)rnd.NextDouble(), (float)rnd.NextDouble(), (float)rnd.NextDouble());
			cb.moveTo(widths[col], heights[line]);
			cb.lineTo(widths[col + 1], heights[line]);
			cb.stroke();
			cb.setRGBColorStrokeF((float)rnd.NextDouble(), (float)rnd.NextDouble(), (float)rnd.NextDouble());
			cb.moveTo(widths[col], heights[line]);
			cb.lineTo(widths[col], heights[line + 1]);
			cb.stroke();
		}
	}
	cb.restoreState();
}

