{
	If target = (If) t;

	target.expr = expr.Clone (clonectx);
	target.TrueStatement = TrueStatement.Clone (clonectx);
	if (FalseStatement != null)
		target.FalseStatement = FalseStatement.Clone (clonectx);
}


