{
	for (int i = 0; i < rowCount; i++)
	{
		if (records[i] == str)
		{
			return i + 1;
		}
	}
	for (int i = 0; i < rowCount; i++)
	{
		if (records[i] == str)
		{
			return i + 1;
		}
	}
	return AddRecord(str);
}

