		args.Add (new Argument (new NullLiteral (loc)));
	else
		args.Add (new Argument (method_group.InstanceExpression));

	args.Add (new Argument (method_group.CreateExpressionTree (ec)));
	Expression e = new Invocation (ma, args).Resolve (ec);
